import {
  Directive, 
  Input, 
  TemplateRef, 
  ViewContainerRef
  } from '@angular/core';

  /**
   * Define expiration date for a given element. If current date is
   * past expiration date, the given element will not be displayed
   * to the user
   * usage: 
   * <div *expires="'2021-01-13T22:30:59">
   * </div>
   * The element above expires after January 13 2021 10:30:59pm at user's local timezone.
   * 
   * You may also define desired timezone in following format:
   * YYYY-MM-DDThh:mm:ss.sssZ
   * example: 2021:01013T22:30:59.000+5:00 // Eastern time
   */
  @Directive({
    selector: '[expires]'
  })
  export class SdsExpiresDirective {

    /** 
     * Expiration date string. This string should be parsable
     * through native Date object
     * */
    @Input() set expires(date: string) {
      const expirationDate = new Date(date);
      const today = new Date();

      const isExpired = expirationDate.valueOf() < today.valueOf();

      if (isExpired) {
        this.viewContainer.clear();
      } else {
        this.viewContainer.createEmbeddedView(this.templateRef);
      }
    }

    constructor(
      private templateRef: TemplateRef<any>,
      private viewContainer: ViewContainerRef
    ) { }

  }

import {
  Directive,
  ElementRef,
  EventEmitter,
  Input,
  OnDestroy,
  Output,
  ViewContainerRef,
  HostBinding,
  HostListener,
} from '@angular/core';
import {
  FocusMonitor,
  FocusOrigin,
  isFakeMousedownFromScreenReader
} from '@angular/cdk/a11y';
import {
  FlexibleConnectedPositionStrategy,
  HorizontalConnectionPos,
  Overlay,
  OverlayConfig,
  OverlayRef,
  VerticalConnectionPos
} from '@angular/cdk/overlay';
import { TemplatePortal } from '@angular/cdk/portal';
import { normalizePassiveListenerOptions } from '@angular/cdk/platform';
import { merge, Subscription } from 'rxjs';
import {
  SdsMenuInterface,
  SdsMenuComponent,
  MenuPositionX,
  MenuPositionY
} from './menu.component';

@Directive({
  selector: '[sdsMenuTriggerFor]'
})
export class SdsMenuTriggerForDirective implements OnDestroy {
  /** ARIA haspopup for the menu trigger. */
  @HostBinding('attr.aria-haspopup') ariaHasPopup = true;

  /** ARIA expanded for the menu trigger. */
  @HostBinding('attr.aria-expanded')
  get menuOpen(): boolean {
    return this._menuOpen;
  }

  /** Holds the menu instance */
  private _menu: SdsMenuInterface;

  /** Holds value for menuOpen variable */
  private _menuOpen = false;

  /** CDK Portal for menu panel */
  private _portal: TemplatePortal;

  /** PortalOutlet */
  private _overlayRef: OverlayRef | null = null;

  /** Tracks input type  */
  private _openedBy: 'mouse' | 'touch' | null = null;

  private _closingActionsSubscription = Subscription.EMPTY;
  private _menuCloseSubscription = Subscription.EMPTY;

  /** References the menu instance that the trigger is associated with. */
  @Input('sdsMenuTriggerFor')
  get menu() {
    return this._menu;
  }
  set menu(menu: SdsMenuInterface) {
    if (menu === this._menu) {
      return;
    }
    this._menu = menu;
    this._menuCloseSubscription.unsubscribe();

    if (menu) {
      this._menuCloseSubscription = menu.closed.asObservable().subscribe(() => {
        this._destroyMenu();
      });
    }
  }

  /** Event emitted when the associated menu is opened. */
  @Output() menuOpened: EventEmitter<void> = new EventEmitter<void>();

  /** Event emitted when the associated menu is closed. */
  @Output() menuClosed: EventEmitter<void> = new EventEmitter<void>();

  /** Handles mouse presses on the trigger. */
  @HostListener('mousedown', ['$event'])
  _handleMousedown(event: MouseEvent): void {
    if (!isFakeMousedownFromScreenReader(event)) {
      // Since right or middle button clicks won't trigger the `click` event,
      // we shouldn't consider the menu as opened by mouse in those cases.
      this._openedBy = event.button === 0 ? 'mouse' : null;
    }
  }

  /** Toggles the menu between the open and closed states. */
  @HostListener('click')
  toggleMenu(): void {
    return this._menuOpen ? this.closeMenu() : this.openMenu();
  }

  constructor(
    private _overlay: Overlay,
    private _element: ElementRef<HTMLElement>,
    private _viewContainerRef: ViewContainerRef,
    private _focusMonitor: FocusMonitor
  ) {
    // On touch devices set _openedBy to 'touch'
    _element.nativeElement.addEventListener(
      'touchstart',
      () => (this._openedBy = 'touch'),
      normalizePassiveListenerOptions({ passive: true })
    );
  }

  ngOnDestroy() {
    if (this._overlayRef) {
      this._overlayRef.dispose();
      this._overlayRef = null;
    }

    this._element.nativeElement.removeEventListener(
      'touchstart',
      () => (this._openedBy = 'touch'),
      normalizePassiveListenerOptions({ passive: true })
    );

    this._menuCloseSubscription.unsubscribe();
    this._closingActionsSubscription.unsubscribe();
  }

  /** Opens the menu. */
  openMenu(): void {
    if (this._menuOpen) {
      return;
    }
    const overlayRef = this._createOverlay();
    const overlayConfig = overlayRef.getConfig();
    this._setPosition(
      overlayConfig.positionStrategy as FlexibleConnectedPositionStrategy
    );
    overlayRef.attach(this._getPortal());
    this._closingActionsSubscription = this._menuClosingActions().subscribe(
      () => this.closeMenu()
    );
    this._initMenu();
    if (this.menu instanceof SdsMenuComponent) {
      this.menu._startAnimation();
    }
  }

  /** Closes the menu. */
  closeMenu(): void {
    this.menu.closed.emit();
  }

  /** Focuses the menu trigger. */
  focus(origin: FocusOrigin = 'program'): void {
    this._focusMonitor.focusVia(this._element, origin);
  }

  /** This method sets the menu state to open and focuses the first item */
  private _initMenu(): void {
    this._setIsMenuOpen(true);
    this.menu.focusFirstItem(this._openedBy || 'program');
  }

  /** sets open state */
  private _setIsMenuOpen(isOpen: boolean): void {
    this._menuOpen = isOpen;
    this._menuOpen ? this.menuOpened.emit() : this.menuClosed.emit();
  }

  /**
   * This method resets the menu when it's closed,
   * most importantly restoring focus to the menu trigger
   */
  private _resetMenu(): void {
    this._setIsMenuOpen(false);

    if (!this._openedBy) {
      this.focus();
    } else {
      this.focus(this._openedBy);
    }

    this._openedBy = null;
  }

  /** Closes the menu and does the necessary cleanup. */
  private _destroyMenu() {
    if (!this._overlayRef || !this.menuOpen) {
      return;
    }

    this._closingActionsSubscription.unsubscribe();

    this._overlayRef.detach();

    if (this.menu instanceof SdsMenuComponent) {
      this.menu._resetAnimation();
      this._resetMenu();
    }

  }

  /**
   * This method creates the overlay from the provided menu's template and saves its
   * OverlayRef so that it can be attached to the DOM when openMenu is called.
   */
  private _createOverlay(): OverlayRef {
    if (!this._overlayRef) {
      const config = this._getOverlayConfig();
      this._subscribeToPositions(
        config.positionStrategy as FlexibleConnectedPositionStrategy
      );
      this._overlayRef = this._overlay.create(config);
      this._overlayRef.keydownEvents().subscribe();
    }
    return this._overlayRef;
  }

  /**
   * This method builds the configuration object needed to create the overlay, the OverlayState.
   * @returns OverlayConfig
   */
  private _getOverlayConfig(): OverlayConfig {
    return new OverlayConfig({
      positionStrategy: this._overlay
        .position()
        .flexibleConnectedTo(this._element)
        .withLockedPosition()
        .withTransformOriginOn('.sds-overlay'),
      hasBackdrop: true,
      backdropClass: 'cdk-overlay-transparent-backdrop',
      scrollStrategy: this._overlay.scrollStrategies.reposition()
    });
  }

  /**
   * Listens to changes in the position of the overlay and sets the correct classes
   * on the menu based on the new position.
   */
  private _subscribeToPositions(
    position: FlexibleConnectedPositionStrategy
  ): void {
    position.positionChanges.subscribe(change => {
      const posX: MenuPositionX =
        change.connectionPair.overlayX === 'start' ? 'after' : 'before';
      const posY: MenuPositionY =
        change.connectionPair.overlayY === 'top' ? 'below' : 'above';

      this.menu.setPositionClasses(posX, posY);
    });
  }

  /**
   * Sets the appropriate positions on a position strategy
   * so the overlay connects with the trigger correctly.
   */
  private _setPosition(positionStrategy: FlexibleConnectedPositionStrategy) {
    const [originX, originFallbackX]: HorizontalConnectionPos[] =
      this.menu.xPosition === 'before' ? ['end', 'start'] : ['start', 'end'];

    const [overlayY, overlayFallbackY]: VerticalConnectionPos[] =
      this.menu.yPosition === 'above' ? ['bottom', 'top'] : ['top', 'bottom'];

    let [originY, originFallbackY] = [overlayY, overlayFallbackY];
    const [overlayX, overlayFallbackX] = [originX, originFallbackX];
    const offsetY = 0;

    if (!this.menu.overlapTrigger) {
      originY = overlayY === 'top' ? 'bottom' : 'top';
      originFallbackY = overlayFallbackY === 'top' ? 'bottom' : 'top';
    }

    // Positions from most to least desirable
    positionStrategy.withPositions([
      { originX, originY, overlayX, overlayY, offsetY },
      {
        originX: originFallbackX,
        originY,
        overlayX: overlayFallbackX,
        overlayY,
        offsetY
      },
      {
        originX,
        originY: originFallbackY,
        overlayX,
        overlayY: overlayFallbackY,
        offsetY: -offsetY
      },
      {
        originX: originFallbackX,
        originY: originFallbackY,
        overlayX: overlayFallbackX,
        overlayY: overlayFallbackY,
        offsetY: -offsetY
      }
    ]);
  }

  /** Returns a stream that emits whenever an action that should close the menu occurs. */
  private _menuClosingActions() {
    const backdrop = this._overlayRef.backdropClick();
    const detachments = this._overlayRef.detachments();
    return merge(backdrop, detachments);
  }

  /** Gets the portal that should be attached to the overlay. */
  private _getPortal(): TemplatePortal {
    if (!this._portal || this._portal.templateRef !== this.menu.templateRef) {
      this._portal = new TemplatePortal(
        this.menu.templateRef,
        this._viewContainerRef
      );
    }
    return this._portal;
  }
}

import {
  Directive,
  Output,
  EventEmitter,
  ElementRef,
  OnInit,
  OnDestroy
} from '@angular/core';
import { ViewportRuler } from '@angular/cdk/overlay';
import { startWith } from 'rxjs/operators';
import { Subscription } from 'rxjs';

/**
 * Directive that triggers a callback whenever the width of
 * its associated element has changed.
 */
@Directive({ selector: '[sdsObserveWidth]' })
export class SdsObserveWidthDirective implements OnInit, OnDestroy {
  /** Event emitted for each change in the element's width. */
  @Output('sdsObserveWidth') elementWidth = new EventEmitter<number>();

  /** Subscription to window resize stream */
  windowResize$: Subscription;

  constructor(
    private _elementRef: ElementRef<HTMLElement>,
    private viewportRuler: ViewportRuler
  ) {}

  ngOnInit() {
    this.windowResize$ = this.viewportRuler
      .change(0)
      .pipe(startWith(this._getElementWidth()))
      .subscribe(() => this._emitEvent());
  }

  ngOnDestroy() {
    this.windowResize$.unsubscribe();
  }

  _emitEvent() {
    const width = this._getElementWidth();
    this.elementWidth.emit(width);
  }

  _getElementWidth(): number {
    return this._elementRef.nativeElement.offsetWidth;
  }
}

import {
  AfterViewInit,
  Directive,
  ElementRef,
  HostListener,
  Input,
  Renderer2,
  TemplateRef,
} from '@angular/core';
import { debounce } from './debounce.decorator';

@Directive({
  selector: '[sdsPopover]',
  exportAs: 'sdsPopover',
})
export class SdsPopoverDirective implements AfterViewInit {
  private _sdsPopoverContent: string | TemplateRef<any> | HTMLParagraphElement;
  private _sdsPopoverTitle: string | TemplateRef<any> | HTMLParagraphElement;

  sdsPopoverDiv: HTMLElement;
  popoverVisible = false;
  popoverDivId: string;

  @HostListener('click', ['$event']) onClick($event: MouseEvent) {
    const clickedOnContent = this.sdsPopoverDiv.contains($event.target as any);
    if (clickedOnContent && !this.closeOnContentClick) {
      return;
    }

    this.clickEvent();
  }

  @HostListener('document:click', ['$event'])
  clickout($event: MouseEvent) {
    if (!this.closeOnClickOutside || !this.popoverVisible) {
      return;
    }

    const clickedInElement = this.el.nativeElement.contains($event.target);
    if (!clickedInElement) {
      this.clickEvent();
    }
  }

  /**
   * Adding listener for keyup.enter to ensure that user can activate popover with keyboard
   */
  @HostListener('keyup.enter', ['$event']) onKeyUp($event: KeyboardEvent) {
    if (
      !this.closeOnContentClick &&
      this.sdsPopoverDiv.contains($event.target as any)
    ) {
      return;
    }
    this.clickEvent();
  }

  /**
   * Adding listener for keydown.space to ensure that user can activate popover with keyboard
   */
  @HostListener('keydown.Space', ['$event']) onKeySpace($event: KeyboardEvent) {
    if (
      !this.closeOnContentClick &&
      this.sdsPopoverDiv.contains($event.target as any)
    ) {
      return;
    }
    this.clickEvent();
    $event.preventDefault();
  }

  @Input()
  position: string = 'top';

  @Input() closeOnContentClick = true;

  @Input() closeOnClickOutside = false;

  constructor(private el: ElementRef, private renderer: Renderer2) {
    this.renderer.addClass(this.el.nativeElement, 'sds-popover');

    this.sdsPopoverDiv = document.createElement('div');
  }

  ngAfterViewInit() {
    // Generating semi-random id for use with aria-describedby
    this.popoverDivId = this.el.nativeElement.id
      ? `${this.el.nativeElement.id}-popover`
      : `${this.el.nativeElement.tagName}-${this.el.nativeElement.offsetTop}-${this.el.nativeElement.offsetWidth}-popover`;
    this.sdsPopoverDiv.id = this.popoverDivId;

    this.renderer.addClass(this.sdsPopoverDiv, 'sds-popover__content');
    this.renderer.addClass(this.sdsPopoverDiv, 'tooltip');
    this.renderer.addClass(this.sdsPopoverDiv, 'out');
    this.renderer.setAttribute(
      this.sdsPopoverDiv,
      'data-position',
      this.position
    );
    this.renderer.setAttribute(this.sdsPopoverDiv, 'aria-hidden', 'true');
    this.renderer.addClass(this.sdsPopoverDiv, this.position);

    // Add title section and divider if title included
    if (this._sdsPopoverTitle) {
      this.renderer.appendChild(this.sdsPopoverDiv, this._sdsPopoverTitle);

      const divider = document.createElement('hr');
      this.renderer.addClass(divider, 'divider');
      this.renderer.appendChild(this.sdsPopoverDiv, divider);
    }

    this.renderer.appendChild(this.sdsPopoverDiv, this._sdsPopoverContent);

    this.renderer.setAttribute(this.el.nativeElement, 'role', 'button');
    this.renderer.setAttribute(this.el.nativeElement, 'aria-expanded', 'false');
    this.renderer.setAttribute(
      this.el.nativeElement,
      'aria-haspopup',
      'dialog'
    );

    this.renderer.appendChild(this.el.nativeElement, this.sdsPopoverDiv);
  }

  @Input()
  set sdsPopover(value: string | TemplateRef<any> | HTMLParagraphElement) {
    this._sdsPopoverContent = this.handlePopoverSection(value, 'content');
  }

  get sdsPopover(): string | TemplateRef<any> | HTMLParagraphElement {
    return this._sdsPopoverContent;
  }

  @Input()
  set sdsPopoverTitle(value: string | TemplateRef<any> | HTMLParagraphElement) {
    this._sdsPopoverTitle = this.handlePopoverSection(value, 'title');
  }

  get sdsPopoverTitle(): string | TemplateRef<any> | HTMLParagraphElement {
    return this._sdsPopoverTitle;
  }

  handlePopoverSection(
    value: string | TemplateRef<any> | HTMLParagraphElement,
    classToApply: string
  ): string | TemplateRef<any> | HTMLParagraphElement {
    let popoverSection;
    if (typeof value === 'string') {
      popoverSection = document.createElement('p');
      popoverSection.innerText = value;
      this.renderer.addClass(popoverSection, classToApply);
    } else {
      popoverSection = value;
    }
    return popoverSection;
  }

  /**
   * Using debounce decorator here to prevent a scenario where a popover is
   * applied to a button and this function is called by both the enter key
   * listener and click listener.
   */
  @debounce(100)
  clickEvent() {
    this.popoverVisible = !this.popoverVisible;
    if (this.popoverVisible) {
      this.renderer.addClass(this.sdsPopoverDiv, 'sds-popover__shown');
      this.renderer.setAttribute(this.sdsPopoverDiv, 'aria-hidden', 'false');
      this.renderer.setAttribute(
        this.el.nativeElement,
        'aria-describedby',
        this.popoverDivId
      );
      this.renderer.setAttribute(
        this.el.nativeElement,
        'aria-expanded',
        'true'
      );
      this.renderer.setAttribute(this.el.nativeElement, 'role', 'none');
      this.renderer.removeClass(this.sdsPopoverDiv, 'sds-popover__hidden');
    } else {
      this.renderer.addClass(this.sdsPopoverDiv, 'sds-popover__hidden');
      this.renderer.setAttribute(this.sdsPopoverDiv, 'aria-hidden', 'true');
      this.renderer.setAttribute(
        this.el.nativeElement,
        'aria-expanded',
        'false'
      );
      this.renderer.setAttribute(this.el.nativeElement, 'role', 'button');

      this.renderer.removeClass(this.sdsPopoverDiv, 'sds-popover__shown');
      this.renderer.removeAttribute(this.el.nativeElement, 'aria-describedby');
    }
  }
}

import { Directive, Input, ElementRef, Renderer2, AfterViewInit, OnInit } from '@angular/core';


@Directive({
  selector: '[sdsPopup]',
  exportAs: 'sdsPopup'
})
export class SdsPopupDirective implements AfterViewInit {
  @Input() sdsPopup: HTMLElement;
  @Input() position: string;
  @Input() placement: string;
  sdsPopupDiv: HTMLElement;

  constructor(private el: ElementRef, private renderer: Renderer2) {
    this.renderer.addClass(this.el.nativeElement, 'sds-popup');
    this.sdsPopupDiv = document.createElement('div');
    this.renderer.addClass(this.sdsPopupDiv, 'sds-popup__content');
  }


  ngAfterViewInit() {
    this.renderer.appendChild(this.el.nativeElement, this.sdsPopup);
    this.renderer.addClass(this.sdsPopupDiv, this.placement);
    this.renderer.addClass(this.sdsPopupDiv, this.position);
    this.renderer.appendChild(this.sdsPopupDiv, this.el.nativeElement.children[0]);
    this.renderer.appendChild(this.el.nativeElement, this.sdsPopupDiv);
  }
}

import {
  Directive, ElementRef, Output,
  EventEmitter, HostListener
} from '@angular/core';


@Directive({
  selector: '[sds-tab-outside]'
})
export class SDSTabOutsideDirective {
  /**
   * Emitter for tabOutside event
   */
  @Output() tabOutside: EventEmitter<any> = new EventEmitter();

  constructor(private _elementRef: ElementRef) { }

  @HostListener('document:keyup', ['$event.target'])
  public hasFocusChanged(target) {
    const isInsideHost = this._elementRef.nativeElement.contains(target);
    if (!isInsideHost) {
      this.tabOutside.emit(undefined);
    }
  }
}

import { AfterViewInit, Directive, ElementRef, HostListener, Input, Renderer2, TemplateRef } from '@angular/core';

@Directive({
  selector: '[sdsTooltip]',
  exportAs: 'sdsTooltip'
})
export class SdsTooltipDirective implements AfterViewInit {

  private _sdsTooltip: string | TemplateRef<any> | HTMLDivElement;
  sdsTooltipDiv: HTMLElement;

  @Input()
  position: string = 'top';

  @HostListener('focus') onFocus() {
    this.renderer.setAttribute(this.sdsTooltipDiv, 'aria-hidden', 'false')
  }
  @HostListener('blur') onBlur() {
    this.renderer.setAttribute(this.sdsTooltipDiv, 'aria-hidden', 'true')
  }


  constructor(private el: ElementRef, private renderer: Renderer2) {
    this.renderer.addClass(this.el.nativeElement, 'sds-tooltip');
    this.renderer.setAttribute(this.el.nativeElement, 'tabindex', '0')

    this.sdsTooltipDiv = document.createElement('div');

    this.renderer.addClass(this.sdsTooltipDiv, 'sds-tooltip__content');
  }

  ngAfterViewInit() {
    this.renderer.setAttribute(this.sdsTooltipDiv, 'data-position', this.position)
    if (this.position && this.sdsTooltip) {
      this.renderer.addClass(this.sdsTooltipDiv, this.position);
      this.renderer.appendChild(this.sdsTooltipDiv, this.sdsTooltip);
      this.renderer.appendChild(this.el.nativeElement, this.sdsTooltipDiv);
    }
  }

  @Input()
  set sdsTooltip(value: string | TemplateRef<any> | HTMLDivElement) {
    if (typeof value === 'string') {
      this._sdsTooltip = document.createElement('div');
      this._sdsTooltip.innerHTML = value;
    } else {
      this._sdsTooltip = value;
    }

    if (this._sdsTooltip) {
      this.renderer.addClass(this._sdsTooltip, 'tooltip')
    }
  }

  get sdsTooltip(): string | TemplateRef<any> | HTMLDivElement {
    return this._sdsTooltip;
  }

}

import { ChangeDetectionStrategy, ChangeDetectorRef, Component, 
  ContentChild, Directive, ElementRef, EventEmitter, Input, NgZone, Output, TemplateRef } from "@angular/core";
import { SdsTreeTableData } from "./tree-table.model";

@Directive({
  selector: `[sdsTreeTableRow]`
})
export class SdsTreeTableRow {
  constructor(
    public templateRef: TemplateRef<any>
  ) {}
}

@Component({
  selector: `sds-tree-table`,
  templateUrl: `./tree-table.component.html`,
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class SdsTreeTableComponent {
  /** Rows of table tada to display */
  @Input() dataSource: SdsTreeTableData[];

  /** Column header text */
  @Input() displayColumns: string[];

  /** 
   * Defines maximum number of children to show to the user
   * IE - if a row has 20 children available, and childrenLimit
   * value is 10, then the amount of children displayed to the
   * user will be numChildrenToDisplay amount, and the rest
   * is toggled behind a 'View All' button,
   * This should be greater than or equal to numChildrenToDisplay
   * @default - Number.MAX_SAFE_INTEGER
   */
  @Input() childrenLimit: number = Number.MAX_SAFE_INTEGER;

  /**
   * Number of children to Display to users if a row's children length
   * exceeds childrenLimit. This should be less than or equal to childrenLimit
   * 
   * General Cases:
   * If numChildrenToDisplay is 5, childrenLimit is 10, and row has 20 children
   *  5 children will be shown and the remaining can be displayed through View All button
   * 
   * If numChildrenToDisplay is 5, childrenLimit is 10, and row has 10 children
   *  All 10 children will be displayed because the row's children does not exceed childrenLimit
   * 
   * If numChildren is 5, childrenLimit is 10, and row has 4 children
   *  All 4 children will be displayed because row's children does not exceed childrenLimit
   * 
   * @default - Number.MAX_SAFE_INTEGER
   */
  @Input() numChildrenToDisplay: number = this.childrenLimit

  /**
   * Reference for content projection. User defined values for how to
   * display each cell in a row
   */
  @ContentChild(SdsTreeTableRow) treetableRow: SdsTreeTableRow;

  /** Emitted for a row if there are more children to display and user clicked view all */
  @Output() viewAll = new EventEmitter<any>();

  /** Emitted anytime a row 's expansion / collapse value changes */
  @Output() rowExpanded = new EventEmitter<any>();

  _selectedRow: any;
  _selectedRowParent: any;

  constructor(
    private elementRef: ElementRef,
    public cdr: ChangeDetectorRef,
    private ngZone: NgZone,
  ) {}


  /**
   * Public Interface - close all opened children
   */
  public collapseAll() {
    this.toggleAllHelper(this.dataSource, false);
    this.cdr.detectChanges();
  }

  /**
   * Public interface - open all panels with children
   */
  public expandAll() {
    this.toggleAllHelper(this.dataSource, true);
    this.cdr.detectChanges();
  }

  /** 
   * Public interface - expand a single row given an ID. 
   * The row's predecessors will also be expanded 
   * @param rowId - id of row to expand
   */
  public expandRow(rowId: string) {
    this.expandRowHelper(this.dataSource, rowId);
    this.cdr.detectChanges();
  }

  /**
   * Public Interface - Collapse a single row given an id.
   * The row's successors will also be collapsed
   * @param rowId - id of row to collapse
   */
  public collapseRow(rowId: string) {
    const row = this.findRow(this.dataSource, rowId);
    if (!row) {
      return;
    }

    this.collapseRowHelper(row);
    this.cdr.detectChanges();
  }

  viewAllClicked(row: SdsTreeTableData, currentRow: HTMLTableRowElement, tableRow: HTMLTableRowElement) {

    currentRow.setAttribute('tabindex', undefined);
    tableRow.setAttribute('tabindex', '0');
    row.viewAllChildren = true;
    setTimeout(() => tableRow.focus());
    if (row.totalChildren && row.totalChildren > row.children.length) {
      this.viewAll.emit(row);
    }
  }

  private toggleAllHelper(data: any[], expanded: boolean) {
    data.forEach(data => {
      if (data.children) {
        this.toggleAllHelper(data.children, expanded);
        data.expanded = expanded;

        if (!expanded) {
          data.viewAllChildren = false;
        }
      }
    });
  }

  private expandRowHelper(allRows: any[], id: string) {
    for (let i = 0; i < allRows.length; i++) {
      const row = allRows[i];
      if (row.id === id) {
        return true;
      } else if (row.children) {
        const isChildExpanded = this.expandRowHelper(row.children, id);
        if (isChildExpanded) {
          row.expanded = true;
          return true;
        }
      }
    }

    return false;
  }

  private findRow(allRows: any[], id: string) {
    for (let i = 0; i < allRows.length; i++) {
      const row = allRows[i];
      if (row.id === id) {
        return row;
      } else if (row.children) {
        return this.findRow(allRows, id);
      }
    }
    return null;
  }

  private collapseRowHelper(row: any) {
    row.expanded = false;
    row.viewAllChildren = false;
    if (row.children) {
      row.children.forEach(child => this.collapseRowHelper(child));
    }
  }

  /** Sets height of vertical border on the tree table view */
  setHeight(row: HTMLTableRowElement, parentRow: HTMLTableRowElement, border: HTMLSpanElement) {
    if (!row || !parentRow) {
      return;
    }

    /** 
     * We run outside ngZone because we don't want the setTimeout to trigger change detection,
     * which would re-run changes on template, and re-evalute this function, causing infinite loop
     */
    this.ngZone.runOutsideAngular(() => {

      /** 
       * We do set timeout to let the table rows finish rendering. If a row was
       * expanded / collapsed, then the height of the vertical border will need to
       * be re-evaluated based on new distance from child to parent. We let the
       * view finish refreshing so that bounding rectangle is re-evaluated, and then
       * we can re-calculate height. Doing this synchronously without setTimeout
       * would give us incorrect value for height because the bouunding rectangle
       * has yet to update from the collapse / expand change
       */
      setTimeout(() => {
        const firstRect = parentRow.getBoundingClientRect();
        const rowRect = row.getBoundingClientRect();

        const yFirstRect = firstRect.top + firstRect.height / 2;
        const yRowRect = rowRect.top + rowRect.height / 2;

        const height = yRowRect - yFirstRect - 20;
        border.style.height = `${height}px`;
        border.style.bottom = `${rowRect.height / 2}px`;
      })
    })
  }

  /** 
   * Defines whether or not to display vertical border from this row. Vertical borders generally
   * start from the last child and extend to the parent. However, if we are truncating the number
   * of children displayed, then the vertical border would need to start from the child we
   * truncate at.
   * */
  displayVerticalBorder(parentRow: SdsTreeTableData, index: number, siblingRows: SdsTreeTableRow[]): boolean {
    if (!siblingRows) return false;

    // Decide whether last child displayed is last child in row's children index or is at numChildrenToDisplay
    if (parentRow.viewAllChildren || siblingRows.length <= this.childrenLimit) {
      return index === siblingRows.length - 1;
    } else {
      return index === this.numChildrenToDisplay - 1;
    }
  }

  getTemplateContext(parent: any, row: any, index: number, level: number, parentSelected?: boolean, parentRow?: HTMLTableRowElement) {
    const updatedLevel = level + 1;
    const posinset = index + 1;
    return {
      row: row,
      level: updatedLevel,
      index: posinset,
      size: parent.children ? parent.children.length : 1,
      rows: parent.children,
      parentSelected: parentSelected,
      parent: parent,
      parentRow: parentRow
    }
  }

  onRowClicked(row: SdsTreeTableData, tableRow: HTMLTableRowElement) {
    if (row.children || row.totalChildren > 0) {
      row.expanded = !row.expanded;
    }

    if (!row.expanded) {
      row.viewAllChildren = false;
    }

    this._selectedRow = row;
    this._selectedRowParent = this.getParentOfRow(this.dataSource, row);
    setTimeout(() => tableRow.focus());
    this.rowExpanded.emit(row);
  }

  onKeyDown($event: KeyboardEvent, tableRow: HTMLTableRowElement) {
    if ($event.target != tableRow) {
      return;
    }

    let siblingRow: HTMLTableRowElement;

    if ($event.key === 'ArrowUp') {
      siblingRow = ($event.target as HTMLTableRowElement).previousElementSibling as HTMLTableRowElement;
    } else if ($event.key === 'ArrowDown') {
      siblingRow = ($event.target as HTMLTableRowElement).nextElementSibling as HTMLTableRowElement;
    } else if ($event.key === 'Home') {
      siblingRow = this.elementRef.nativeElement.querySelector('tbody tr');
    } else if ($event.key === 'End') {
      siblingRow = this.elementRef.nativeElement.querySelector('tbody tr:last-child');
    }

    if (!siblingRow) return;

    ($event.target as HTMLTableRowElement).setAttribute('tabindex', undefined);
    siblingRow.setAttribute('tabindex', '0');
    siblingRow.focus();
    $event.preventDefault();
  }

  getParentOfRow(allRows: any[], row: any) {
    let retRow = null;
    for (let i = 0; i < allRows.length; i++) {
      if (allRows[i] === row) {
        retRow = allRows[i];
        break;
      } else if(allRows[i].children) {
        const isChildRow = this.getParentOfRow(allRows[i].children, row);
        if (isChildRow) {
          retRow = allRows[i];
        }
      }
    }

    return retRow;
  }
}

import {
  Directive,
  ElementRef,
  OnInit,
  Input,
  OnDestroy,
  AfterViewInit,
  HostListener,
  Injector,
  ViewContainerRef
} from '@angular/core';
import { coerceNumberProperty } from '@angular/cdk/coercion';
import {
  ViewportRuler,
  OverlayConfig,
  Overlay,
  OverlayRef,
  ConnectedPosition
} from '@angular/cdk/overlay';
import { Subscription, merge } from 'rxjs';
import { startWith } from 'rxjs/operators';
import { PortalInjector, ComponentPortal } from '@angular/cdk/portal';
import { SdsTruncatedTextContainerComponent } from './truncate-text-container.component';
import { SDS_TRUNCATED_TEXT_DATA } from './truncates-text-base';

@Directive({ selector: '[sdsTruncateTextByLine]' })
export class SdsTruncateTextByLineDirective
  implements OnInit, OnDestroy, AfterViewInit {
  /** Maximum lines of text limit */
  @Input('sdsTruncateTextByLine')
  get textLinesLimit(): any {
    return this._textLinesLimit;
  }
  set textLinesLimit(_textLinesLimit: any) {
    _textLinesLimit = coerceNumberProperty(_textLinesLimit);
    if (this._textLinesLimit !== _textLinesLimit) {
      this._textLinesLimit = _textLinesLimit;
    }
  }
  private _textLinesLimit: number;

  /** PortalOutlet */
  private _overlayRef: OverlayRef | null = null;

  /** Holds subscription to stream of overlay closing events */
  private _closingActionsSubscription = Subscription.EMPTY;

  /** Holds initial text */
  private initialText: string;

  /** Subscription to window resize stream */
  windowResize$: Subscription;

  /** Approximated character width of the host text */
  private approximatedCharacterWidth: number;

  constructor(
    private _overlay: Overlay,
    private _injector: Injector,
    private _element: ElementRef,
    private _viewportRuler: ViewportRuler,
    private _viewContainerRef: ViewContainerRef
  ) {}

  ngOnInit() {
    this.initialText = this._element.nativeElement.innerText.trim();

    // Clone element to facilitate calculations
    const hostCloneEl = this._element.nativeElement.cloneNode() as HTMLElement;

    // Add 1 character to calculate character width
    hostCloneEl.innerHTML = 'x';

    // Render the clone to get character width
    this._element.nativeElement.parentElement.appendChild(hostCloneEl);

    // Set the clone to inline to prevent cases where the clone
    // expands to 100% width of the container
    hostCloneEl.setAttribute('style', 'display: inline');

    // These are close approximations that are used to better guess
    // how many characters fit in X number of lines
    this.approximatedCharacterWidth = hostCloneEl.offsetWidth;

    // Remove clone after calculations
    hostCloneEl.remove();
  }

  ngAfterViewInit(): void {
    this.windowResize$ = this._viewportRuler
      .change(0)
      .pipe(startWith('Start'))
      .subscribe(() => this.updateUI());
  }

  ngOnDestroy(): void {
    if (this._overlayRef) {
      this._overlayRef.dispose();
    }
    this._closingActionsSubscription.unsubscribe();
    this.windowResize$.unsubscribe();
  }

  /** Configures and creates the CDK overlay */
  private _createOverlay() {
    const overlayPositions: ConnectedPosition = {
      originX: 'start',
      originY: 'bottom',
      overlayX: 'start',
      overlayY: 'top'
    };
    const config = new OverlayConfig({
      positionStrategy: this._overlay
        .position()
        .flexibleConnectedTo(this._element)
        .withLockedPosition()
        .withPositions([overlayPositions])
        .withTransformOriginOn('.sds-overlay'),
      hasBackdrop: true,
      backdropClass: 'cdk-overlay-transparent-backdrop',
      scrollStrategy: this._overlay.scrollStrategies.close()
    });
    return this._overlay.create(config);
  }

  /** Attach a ComponentPortal to the overlay **/
  private _attachContainer(overlay: OverlayRef) {
    const injector = new PortalInjector(
      this._injector,
      new WeakMap([[SDS_TRUNCATED_TEXT_DATA, { text: this.initialText }]])
    );
    const containerPortal = new ComponentPortal(
      SdsTruncatedTextContainerComponent,
      this._viewContainerRef,
      injector
    );
    const containerRef = overlay.attach(containerPortal);

    return containerRef.instance;
  }

  /** Returns a stream that emits whenever an action that should close the overlay occurs. */
  private _overlayClosingActions() {
    const backdrop = this._overlayRef.backdropClick();
    const detachments = this._overlayRef.detachments();
    return merge(backdrop, detachments);
  }

  /** Width of host element */
  private _getHostWidth(): number {
    return this._element.nativeElement.offsetWidth;
  }

  /** Approximated number of characters that are visible in the container */
  private _getVisibleCharacters(): number {
    return Math.floor(
      (this._getHostWidth() / this.approximatedCharacterWidth) *
        this.textLinesLimit
    );
  }

  private _isNotLongEnough(): boolean {
    return this._getVisibleCharacters() > this.initialText.length;
  }

  @HostListener('click')
  openOverlay(): void {
    // Exit if all text can be visible in container
    if (this._isNotLongEnough()) return;

    this._overlayRef = this._createOverlay();
    const container = this._attachContainer(this._overlayRef);
    this._closingActionsSubscription = this._overlayClosingActions().subscribe(
      () => this.closeOverlay()
    );
    // Wait for the next event loop tick to start the animation
    setTimeout(() => {
      container.startAnimation();
    });
  }

  updateUI() {
    // Exit if all text can be visible in container
    if (this._isNotLongEnough()) return;

    const wordCut = false;
    const ellipsis = '...';
    const limit = this._getVisibleCharacters() - ellipsis.length;

    let visibleText = this.initialText.slice(0, limit);

    if (!wordCut) {
      const isEndofWord = this.initialText.substr(limit, limit + 1) === ' ';
      if (!isEndofWord) {
        const previousWord = visibleText.lastIndexOf(' ');
        visibleText = visibleText.slice(0, previousWord);
      }
    }

    this._element.nativeElement.innerText = visibleText + ellipsis;
  }

  closeOverlay() {
    this._closingActionsSubscription.unsubscribe();
    this._overlayRef.detach();
  }
}

