import { AnimationEvent } from '@angular/animations';
import { CdkAccordionItem } from "@angular/cdk/accordion";
import { UniqueSelectionDispatcher } from '@angular/cdk/collections';
import { TemplatePortal } from '@angular/cdk/portal';
import {
  AfterContentInit,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  ContentChild,
  EventEmitter,
  ElementRef,
  Inject,
  OnChanges,
  OnDestroy,
  Optional,
  Output,
  SimpleChanges,
  SkipSelf,
  ViewContainerRef,
  ViewEncapsulation,
  ViewChild
} from "@angular/core";
import { DOCUMENT } from '@angular/common';
import { ANIMATION_MODULE_TYPE } from '@angular/platform-browser/animations';
import { Subject } from 'rxjs';
import { filter, startWith, take, distinctUntilChanged } from 'rxjs/operators';
import { sdsExpansionAnimations } from './accordion-animations';
import { SdsAccordionItemContentDirective } from './accordion-item-content.directive';
import { SDS_ACCORDION, SdsAccordionBase } from './accordion-base';

/** Accordion Item's states. */
export type SdsAccordionItemState = 'expanded' | 'collapsed';

/** Counter for generating unique element ids. */
let uniqueId = 0;

@Component({
  selector: "sds-accordion-item",
  exportAs: "sdsAccordionItem",
  templateUrl: "accordion-item.component.html",
  encapsulation: ViewEncapsulation.None,
  changeDetection: ChangeDetectionStrategy.OnPush,
  inputs: ['disabled', 'expanded'],
  outputs: ['opened', 'closed', 'expandedChange'],
  animations: [sdsExpansionAnimations.bodyExpansion],
  host: {
    'class': 'sds-accordion__item',
    '[class.sds-accordion__item--expanded]': 'expanded',
    '[class._sds-animation-noopable]': '_animationMode === "NoopAnimations"'
  }
})
export class SdsAccordionItemComponent extends CdkAccordionItem
  implements AfterContentInit, OnChanges, OnDestroy {
  private _document: Document;

  /** An event emitted after the body's expansion animation happens. */
  @Output() afterExpand = new EventEmitter<void>();

  /** An event emitted after the body's collapse animation happens. */
  @Output() afterCollapse = new EventEmitter<void>();

  /** Stream that emits for changes in `@Input` properties. */
  readonly _inputChanges = new Subject<SimpleChanges>();

  /** Optionally defined accordion the accordion item belongs to. */
  accordion: SdsAccordionBase;

  /** Content that will be rendered lazily. */
  @ContentChild(SdsAccordionItemContentDirective) _lazyContent: SdsAccordionItemContentDirective;

  /** Element containing the accordion item's user-provided content. */
  // @ViewChild('body') _body: ElementRef<HTMLElement>;
  @ViewChild('body', { static: false }) _body: ElementRef<HTMLElement>;

  /** Portal holding the user's content. */
  _portal: TemplatePortal;

  /** ID for the associated header element. Used for a11y labelling. */
  _headerId = `sds-accordion-item-header-${uniqueId++}`;

  /** Stream of body animation done events. */
  _bodyAnimationDone = new Subject<AnimationEvent>();

  constructor(@Optional() @SkipSelf() @Inject(SDS_ACCORDION) accordion: SdsAccordionBase,
    _changeDetectorRef: ChangeDetectorRef,
    _uniqueSelectionDispatcher: UniqueSelectionDispatcher,
    private _viewContainerRef: ViewContainerRef,
    @Inject(DOCUMENT) _document: any,
    @Optional() @Inject(ANIMATION_MODULE_TYPE) public _animationMode: string) {
    super(accordion, _changeDetectorRef, _uniqueSelectionDispatcher);
    this.accordion = accordion;
    this._document = _document;

    // We need a Subject with distinctUntilChanged, because the `done` event
    // fires twice on some browsers. See https://github.com/angular/angular/issues/24084
    this._bodyAnimationDone.pipe(distinctUntilChanged((x, y) => {
      return x.fromState === y.fromState && x.toState === y.toState;
    })).subscribe(event => {
      if (event.fromState !== 'void') {
        if (event.toState === 'expanded') {
          this.afterExpand.emit();
        } else if (event.toState === 'collapsed') {
          this.afterCollapse.emit();
        }
      }
    });

    console.warn(`This is a deprectaed version of accordion component, and will be removed in future versions. 
      Please switch to using usa-accordion from @gsa-sam/ngx-uswds (https://github.com/GSA/ngx-uswds)`);
  }

  /** Gets the expanded state string. */
  _getExpandedState(): SdsAccordionItemState {
    return this.expanded ? 'expanded' : 'collapsed';
  }

  ngAfterContentInit() {
    if (this._lazyContent) {
      // Render the content as soon as the accordion item becomes open.
      this.opened.pipe(
        startWith(null!),
        filter(() => this.expanded && !this._portal),
        take(1)
      ).subscribe(() => {
        this._portal = new TemplatePortal(this._lazyContent._template, this._viewContainerRef);
      });
    }
  }

  ngOnChanges(changes: SimpleChanges) {
    this._inputChanges.next(changes);
  }

  ngOnDestroy() {
    super.ngOnDestroy();
    this._bodyAnimationDone.complete();
    this._inputChanges.complete();
  }

  /** Checks whether the accordion item's content contains the currently-focused element. */
  _containsFocus(): boolean {
    if (this._body) {
      const focusedElement = this._document.activeElement;
      const bodyElement = this._body.nativeElement;
      return focusedElement === bodyElement || bodyElement.contains(focusedElement);
    }

    return false;
  }
}

import { FocusMonitor, FocusableOption, FocusOrigin } from "@angular/cdk/a11y";
import { ENTER, SPACE, hasModifierKey } from "@angular/cdk/keycodes";
import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  ElementRef,
  Host,
  OnDestroy,
  ViewEncapsulation
} from "@angular/core";
import { merge, Subscription } from "rxjs";
import { filter } from "rxjs/operators";
import { SdsAccordionItemComponent } from "./accordion-item.component";

@Component({
  selector: "sds-accordion-item-header",
  templateUrl: "./accordion-item-header.component.html",
  styleUrls:['./accordion-item-header.component.scss'],
  encapsulation: ViewEncapsulation.None,
  changeDetection: ChangeDetectionStrategy.OnPush,
  host: {
    class: "sds-accordion__trigger",
    role: "button",
    "[attr.id]": "accordionItem._headerId",
    "[attr.tabindex]": "disabled ? -1 : 0",
    "[attr.aria-controls]": "_getAccordionItemId()",
    "[attr.aria-expanded]": "_isExpanded()",
    "[attr.aria-disabled]": "accordionItem.disabled",
    "(click)": "_toggle()",
    "(keydown)": "_keydown($event)"
  }
})
export class SdsAccordionItemHeaderComponent implements OnDestroy, FocusableOption {
  private _parentChangeSubscription = Subscription.EMPTY;
  constructor(
    @Host() public accordionItem: SdsAccordionItemComponent,
    private _element: ElementRef,
    private _focusMonitor: FocusMonitor,
    private _changeDetectorRef: ChangeDetectorRef
  ) {

    // Since the toggle state depends on an @Input on the accordion item, we
    // need to subscribe and trigger change detection manually.
    this._parentChangeSubscription = merge(
      accordionItem.opened,
      accordionItem.closed,
      accordionItem._inputChanges.pipe(
        filter(changes => !!(changes["disabled"]))
      )
    ).subscribe(() => this._changeDetectorRef.markForCheck());

    _focusMonitor.monitor(_element).subscribe(origin => {
      if (origin && accordionItem.accordion) {
        accordionItem.accordion._handleHeaderFocus(this);
      }
    });

  }

  /**
   * Whether the associated accordion item is disabled. 
   * Implemented as a part of `FocusableOption`.
   */
  get disabled() {
    return this.accordionItem.disabled;
  }

  /** Toggles the expanded state of the accordion item. */
  _toggle(): void {
    this.accordionItem.toggle();
  }

  /** Gets whether the accordion item is expanded. */
  _isExpanded(): boolean {
    return this.accordionItem.expanded;
  }

  /** Gets the accordion item id. */
  _getAccordionItemId(): string {
    return this.accordionItem.id;
  }

  /** Handle keydown event calling to toggle() if appropriate. */
  _keydown(event: KeyboardEvent) {
    switch (event.keyCode) {
      // Toggle for space and enter keys.
      case SPACE:
      case ENTER:
        if (!hasModifierKey(event)) {
          event.preventDefault();
          this._toggle();
        }

        break;
      default:
        if (this.accordionItem.accordion) {
          this.accordionItem.accordion._handleHeaderKeydown(event);
        }

        return;
    }
  }

  /**
   * Focuses the item header. Implemented as a part of `FocusableOption`.
   * @param origin Origin of the action that triggered the focus.
   * @docs-private
   */
  focus(origin: FocusOrigin = "program") {
    this._focusMonitor.focusVia(this._element, origin);
  }

  ngOnDestroy() {
    this._parentChangeSubscription.unsubscribe();
    this._focusMonitor.stopMonitoring(this._element);
  }
}

import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'sds-actions-menu',
  templateUrl: 'actions-menu.component.html'
})
export class SdsActionsMenuComponent {
  @Input() model;
  @Input() size: string;
  @Output() clicks = new EventEmitter<string>();
  constructor() {}
}

import {
  Component,
  Input,
  ViewChild,
  TemplateRef,
  ElementRef,
  forwardRef,
  HostListener,
  ChangeDetectorRef,
  ChangeDetectionStrategy,
  OnInit,
  OnDestroy
} from '@angular/core';

import {
  NG_VALUE_ACCESSOR,
  ControlValueAccessor,
  FormControl
} from '@angular/forms';
import { SDSSelectedItemModel } from '../selected-result/models/sds-selectedItem.model';
import { SDSAutocompleteServiceInterface } from '../autocomplete-search/models/SDSAutocompleteServiceInterface';
import { SDSAutocompletelConfiguration } from './models/SDSAutocompletelConfiguration.model';
import { SelectionMode } from '../selected-result/models/sds-selected-item-model-helper';
import { SDSAutocompleteSearchComponent } from '../autocomplete-search/autocomplete-search.component';
import { Subscription } from 'rxjs';

const Autocomplete_VALUE_ACCESSOR: any = {
  provide: NG_VALUE_ACCESSOR,
  useExisting: forwardRef(() => SDSAutocompleteComponent),
  multi: true
};

@Component({
  selector: 'sds-autocomplete',
  templateUrl: './autocomplete.component.html',
  styleUrls: ['./autocomplete.component.scss'],
  providers: [Autocomplete_VALUE_ACCESSOR],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class SDSAutocompleteComponent implements ControlValueAccessor, OnInit, OnDestroy {
  /**
   * Allow to insert a customized template for suggestions results
   */
  @Input() suggestionTemplate: TemplateRef<any>;

  /**
   * Allow to insert a customized template for selected items
   */
  @Input() selectedItemTemplate: TemplateRef<any>;

  /**
   * The data model that has the selected item
   */
  public model: SDSSelectedItemModel = new SDSSelectedItemModel();

  public disabled: boolean;

  /**
   * Configuration for the control
   */
  @Input()
  public configuration: SDSAutocompletelConfiguration;

  /**
   * Model contain only the primary key, primary value, and secondary value.
   */
  @Input()
  public essentialModelFields: boolean = false;

  /**
   * Instance of the SamHiercarchicalServiceInterface provided
   */
  @Input()
  public service: SDSAutocompleteServiceInterface;

  _subscriptions = new Subscription();

  @ViewChild('autocompleteSearch', { static: true }) autocompleteSearch: SDSAutocompleteSearchComponent;
  constructor(private cd: ChangeDetectorRef) {  }

  ngOnInit() {
    if (!this.configuration.registerChanges$) {
      return;
    }

    const changesSubscription = this.configuration.registerChanges$.subscribe(() => {
      this.cd.detectChanges();
    });

    this._subscriptions.add(changesSubscription);
  }

  ngOnDestroy() {
    this._subscriptions.unsubscribe();
  }

  /**
   * Stored Event for ControlValueAccessor
   */
  @HostListener('focusout')
  public onTouched = () => { };

  /**
   * Stored Event for ControlValueAccessor
   */
  public onChange = (_: any) => { };

  // ControlValueAccessor (and Formly) is trying to update the value of the FormControl (our custom component) programatically
  // If there is a value we will just overwrite items
  // If there is no value we reset the items array to be empty
  writeValue(value: any) {
    if (value instanceof SDSSelectedItemModel && value.items && value.items.length) {
      this.model.items = [...value.items];
      this.cd.markForCheck();
    }
    else if (value && value.length) {
      this.model.items = value;
      this.cd.markForCheck();
    } else {
      if (!this.model || !(this.model instanceof SDSSelectedItemModel)) {
        this.model = new SDSSelectedItemModel();
      }
      this.model.items = value && value.items ? value.items : [];
      this.cd.markForCheck();
    }

    this.autocompleteSearch.writeValue(this.model);
  }

  // Method that is fired when the child component event notifies us that the items array has been modified within the child component
  updateItems($event) {
    this.updateModel();
  }

  // Helper method that gets a new instance of the model and notifies ControlValueAccessor that we have a new model for this FormControl (our custom component)
  updateModel() {
    const model = this.getModel();
    this.onChange(model);
  }

  // Helper method to return a new instance of an array that contains our items
  getModel() {
    return [...this.model.items];
  }

  // ControlValueAccessor hook that lets us call this._onChange(var) to let the form know our variable has changed (in this case model)
  registerOnChange(fn: any): void {
    this.onChange = fn;
  }

  // ControlValueAccessor hook (not used)
  registerOnTouched(fn: any) {
    this.onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
    this.cd.detectChanges();
  }

  isSingleMode(): boolean {
    if (this.configuration) {
      return this.configuration.selectionMode === SelectionMode.SINGLE;
    } else {
      return false;
    }
  }
  addItem(item: object) {
    this.autocompleteSearch.selectItem(item);
  }

  addItems(list: object[]) {
    list.forEach(item => {
      this.addItem(item);
    })
  }
}

import {
  Component,
  Input,
  ViewChild,
  TemplateRef,
  ElementRef,
  forwardRef,
  ChangeDetectorRef,
} from '@angular/core';
import { NG_VALUE_ACCESSOR, ControlValueAccessor } from '@angular/forms';
import { SDSAutocompleteServiceInterface } from './models/SDSAutocompleteServiceInterface';
import { KeyHelper, KEYS } from '../key-helper/key-helper';
import { SDSSelectedItemModel } from '../selected-result/models/sds-selectedItem.model';
import {
  SelectionMode,
  SDSSelectedItemModelHelper,
} from '../selected-result/models/sds-selected-item-model-helper';

import { SDSAutocompleteSearchConfiguration } from './models/SDSAutocompleteConfiguration';

const Autocomplete_Autocomplete_VALUE_ACCESSOR: any = {
  provide: NG_VALUE_ACCESSOR,
  useExisting: forwardRef(() => SDSAutocompleteSearchComponent),
  multi: true,
};

@Component({
  selector: 'sds-search-autocomplete',
  templateUrl: './autocomplete-search.component.html',
  styleUrls: ['./autocomplete-search.component.scss'],
  providers: [Autocomplete_Autocomplete_VALUE_ACCESSOR],
})
export class SDSAutocompleteSearchComponent implements ControlValueAccessor {
  constructor(private _changeDetectorRef: ChangeDetectorRef) {}
  /**
   * Ul list of elements
   */
  @ViewChild('resultsList') resultsListElement: ElementRef;

  /**
   * input control
   */
  @ViewChild('input') input: ElementRef;

  /**
   * Allow to insert a customized template for suggestions to use
   */
  @Input() itemTemplate: TemplateRef<any>;

  /**
   * The data model that has the selected item
   */
  public model: SDSSelectedItemModel;

  /**
   * Model contain only the primary key, primary value, and secondary value.
   */
  @Input() public essentialModelFields: boolean = false;

  /**
   * Configuration for the Autocomplete control
   */
  @Input()
  public configuration: SDSAutocompleteSearchConfiguration;

  /**
   * Instance of the SamHiercarchicalServiceInterface provided
   */
  @Input()
  public service: SDSAutocompleteServiceInterface;

  /**
   * Timer id for the timer awaiting the service call for more typeing
   */
  private timeoutNumber: number;

  /**
   *  result set to be rendered
   */
  results: object[];

  /**
   * max number of results to be shown
   */
  private maxResults: number;

  /**
   * selected index
   */
  public highlightedIndex: number = 0;

  /**
   * selected child index
   */
  public highlightedChildIndex = 0;

  /**
   * highlighted object in drop down
   */
  private highlightedItem: object;

  /**
   * value of the input field
   */
  public inputValue: string = '';

  /**
   * Proprty being set on the object is highlighted
   */
  private HighlightedPropertyName = 'highlighted';

  public showLoad: boolean = true;

  /**
   * Search string
   */
  private searchString: string = null;

  /**
   * Message announced by screen readers when
   * autocomplete results are updated or new item
   * is highlighted
   */
  public srOnlyText: string;

  /**
   * To make input readonly
   */
  @Input() public inputReadOnly = false;

  /**
   * Stored Event for ControlValueAccessor
   */
  public onTouchedCallback: () => void = () => null;

  /**
   * Stored Event for ControlValueAccessor
   */
  public propogateChange: (_: any) => void = (_: any) => null;

  @Input()
  public disabled: boolean;

  private resultsAvailableMessage: string =
    ' results available. Use up and down arrows\
  to scroll through results. Hit enter to select.';

  private index = 0;

  /**
   * Gets the string value from the specifed properties of an object
   * @param object
   * @param propertyFields comma seperated list with periods depth of object
   */

  getObjectValue(object: Object, propertyFields: string): string {
    let value = '';
    let current = object;
    let fieldSplit = propertyFields.split(',');
    for (let i = 0; i < fieldSplit.length; i++) {
      let fieldValue = fieldSplit[i];
      let fieldPartSplit = fieldValue.split('.');
      for (let j = 0; j < fieldPartSplit.length; j++) {
        let fieldCheckValue = fieldPartSplit[j];
        if (current) {
          current = current[fieldCheckValue];
        }
      }

      if (current) {
        value += current.toString() + ' ';
      }
      current = object;
    }
    return value.trim();
  }

  /**
   * Determines if the dropdown should be shown
   */
  public showResults = false;

  /**
   * Clears the input fields and value
   */
  public clearInput(): void {
    this.inputValue = '';
    if (this.configuration.selectionMode === SelectionMode.SINGLE) {
      if (this.model.items.length > 0) {
        SDSSelectedItemModelHelper.clearItems(this.model.items);
        this.propogateChange(this.model);
      }
    }
    this.onTouchedCallback();
    this.clearAndHideResults();
  }

  /**
   *
   * @param event
   */
  checkForFocus(event): void {
    this.focusRemoved();
    this.showResults = false;
  }

  /**
   *
   */
  private focusRemoved() {
    if (this.configuration) {
      if (this.configuration.selectionMode === SelectionMode.SINGLE) {
        if (this.model.items.length > 0) {
          this.inputValue = this.getObjectValue(
            this.model.items[0],
            this.configuration.primaryTextField
          );
        }
      } else {
        this.inputValue = '';
      }
    } else {
      this.inputValue = '';
    }
  }

  onkeypress(ev) {
    return this.configuration.inputReadOnly ? false : true;
  }
  textChange(event) {
    if (!this.configuration.isTagModeEnabled) {
      // ie 11 placeholders will incorrectly trigger input events (known bug)
      // if input isn't active element then don't do anything
      if (event.target != document.activeElement) {
        event.preventDefault();
        return;
      }
      const searchString = event.target.value || '';
      this.getResults(searchString);
    }
  }
  isClearIconVisible(): boolean {
    if (
      this.model &&
      this.model.items.length > 0 &&
      this.configuration.hideCloseIcon
    )
      return false;

    return this.inputValue && !this.disabled;
  }
  //
  /**
   * Event method used when focus is gained to the input
   */
  inputFocusHandler(): void {
    if (!this.configuration.isTagModeEnabled) {
      if (this.configuration.focusInSearch) {
        this.highlightedIndex = 0;
        this.highlightedChildIndex = this.configuration.isSelectableGroup
          ? 0
          : null;
        this.getResults(this.inputValue || '');
      }
      this.onTouchedCallback();
      if (this.isAutocompleteWithinModal()) {
        this.addListener();
      }
    }
  }
  isAutocompleteWithinModal() {
    return document.getElementsByClassName('sds-dialog-content').length > 0;
  }

  /**
   * Key event
   * @param event
   */
  onKeydown(event): void {
    if (KeyHelper.is(KEYS.ALT, event)) {
      event.preventDefault();
      this.inputFocusHandler();
    }
    if (KeyHelper.is(KEYS.TAB, event)) {
      return;
    } else if (KeyHelper.is(KEYS.BACKSPACE, event)) {
      if (this.configuration.inputReadOnly) {
        event.preventDefault();
      }
    } else if (KeyHelper.is(KEYS.DOWN, event)) {
      event.preventDefault();
      this.onArrowGroupDown();
    } else if (KeyHelper.is(KEYS.UP, event)) {
      event.preventDefault();
      this.onArrowGroupUp();
    } else if (KeyHelper.is(KEYS.ENTER, event) && this.highlightedIndex >= 0) {
      if (this.configuration.isTagModeEnabled && this.inputValue.length > 0) {
        const val = this.createFreeTextItem();
        this.selectItem(val);
      } else {
        this.selectItem(this.results[this.highlightedIndex]);
      }
    } else if (KeyHelper.is(KEYS.ENTER, event) && this.highlightedIndex < 0) {
      if (this.configuration.isFreeTextEnabled && this.inputValue.length > 0) {
        const val = this.createFreeTextItem();
        this.selectItem(val);
      }
    } else if (KeyHelper.is(KEYS.ESC, event)) {
      if (this.showResults) {
        this.clearAndHideResults();
        if (event.stopPropagation) {
          event.stopPropagation();
        }
      }
    }
  }

  /**
   * selects the item adding it to the model and closes the results
   * @param item
   */
  public selectItem(item: object): void {
    let filterItem = {};
    if (this.essentialModelFields) {
      filterItem[this.configuration.primaryKeyField] =
        item[this.configuration.primaryKeyField];
      filterItem[this.configuration.primaryTextField] =
        item[this.configuration.primaryTextField];
      if (this.configuration.secondaryTextField) {
        filterItem[this.configuration.secondaryTextField] =
          item[this.configuration.secondaryTextField];
      }
    } else {
      filterItem = item;
    }
    SDSSelectedItemModelHelper.addItem(
      filterItem,
      this.configuration.primaryKeyField,
      this.configuration.selectionMode,
      this.model
    );
    this.propogateChange(this.model);
    let message = this.getObjectValue(
      item,
      this.configuration.primaryTextField
    );
    this.inputValue = message;
    this.focusRemoved();
    this.showResults = false;
  }

  /**
   *  clears the results and closes result drop down
   */
  private clearAndHideResults(): void {
    this.results = [];
    this.showResults = false;
    this.focusRemoved();
  }

  openOptions() {
    this.input.nativeElement.focus();
  }

  public getFlatElements() {
    const results = this.results;
    const flat = [];
    const flatten = (array: any) => {
      for (let i in array) {
        const item = array[i];
        flat.push(item);
        if (
          item[this.configuration.groupByChild] &&
          item[this.configuration.groupByChild].length
        ) {
          flatten(item[this.configuration.groupByChild]);
        }
      }
    };
    flatten(results);
    return flat;
  }

  /**
   * When paging up and down with arrow key it sets the highlighted item into view
   */
  private scrollToSelectedItem() {
    if (this.highlightedIndex >= 0) {
      this._changeDetectorRef.detectChanges();
      const dom = this.resultsListElement.nativeElement;
      const selectedChild = dom.querySelector(
        '.sds-autocomplete__item--highlighted'
      );
      if (selectedChild) {
        // Manually set scroll top rather than invoke scroll functions for browser compatibility
        const containerCenter =
          this.resultsListElement.nativeElement.getBoundingClientRect().height /
          2;
        this.resultsListElement.nativeElement.scrollTop =
          selectedChild.offsetTop - containerCenter;
      }
    }
  }

  /**
   *  handles the arrow down key event
   */
  private onArrowGroupDown(): void {
    if (this.results && this.results.length > 0) {
      const flat = this.getFlatElements();
      if (this.highlightedIndex < flat.length - 1) {
        this.highlightedIndex++;
      }
      this.setHighlightedItem(flat[this.highlightedIndex]);
      this.scrollToSelectedItem();
    }
  }

  /**
   *  handles the arrow up key event
   */
  private onArrowGroupUp(): void {
    if (this.results && this.results.length > 0) {
      const flat = this.getFlatElements();
      if (this.highlightedIndex != 0) {
        this.highlightedIndex--;
      }
      this.setHighlightedItem(flat[this.highlightedIndex]);
      this.scrollToSelectedItem();
    }
  }
  showFreeText() {
    if (this.configuration.isFreeTextEnabled) {
      if (this.inputValue) {
        if (this.inputValue.length !== 0) {
          let foundItem = false;
          if (this.results) {
            for (var i = 0; i < this.results.length && !foundItem; i++) {
              let item = this.results[i];
              foundItem =
                item[this.configuration.primaryTextField] === this.inputValue;
            }
          }
          if (this.model.items.length > 0 && !foundItem) {
            for (var i = 0; i < this.model.items.length && !foundItem; i++) {
              let item = this.model.items[i];
              foundItem =
                item[this.configuration.primaryTextField] === this.inputValue;
            }
          }

          return !foundItem;
        } else {
          return false;
        }
      } else {
        return false;
      }
    } else {
      return this.configuration.isFreeTextEnabled;
    }
  }

  private createFreeTextItem() {
    let item = { type: 'custom' };
    item[this.configuration.primaryTextField] = this.inputValue;
    item[this.configuration.primaryKeyField] = this.inputValue;
    return item;
  }
  /**
   *  return Item is already selected or not
   * @param result
   */
  checkItemSelected(result: any) {
    const selectedItem = this.model.items.filter(
      (item) =>
        item[this.configuration.primaryKeyField] ===
        result[this.configuration.primaryKeyField]
    );
    return selectedItem.length > 0 ? true : false;
  }
  /**
   *  gets the inital results
   * @param searchString
   */
  private getResults(searchString: string): void {
    if (searchString.length >= this.configuration.minimumCharacterCountSearch) {
      if (
        !this.matchPastSearchString(searchString) ||
        (this.matchPastSearchString(searchString) && !this.showResults) ||
        this.matchPastSearchString('')
      ) {
        this.searchString = searchString;
        window.clearTimeout(this.timeoutNumber);
        this.timeoutNumber = window.setTimeout(() => {
          this.showLoad = true;
          this.service.getDataByText(0, searchString).subscribe((result) => {
            this.results = result.items;
            this.showLoad = false;
            this.maxResults = result.totalItems;

            this.highlightedIndex =
              this.configuration.isFreeTextEnabled || this.maxResults == 0
                ? -1
                : 0;
            if (!this.configuration.isFreeTextEnabled) {
              this.setHighlightedItem(this.results[this.highlightedIndex]);
            }
            this.showResults = true;
            this.addScreenReaderMessage(
              this.maxResults + ' ' + this.resultsAvailableMessage
            );
            this._changeDetectorRef.markForCheck();
          });
        }, this.configuration.debounceTime);
      }
    }
  }

  /**
   * Checks if the new search string matches the old search string
   * @param searchString
   */
  private matchPastSearchString(searchString: string) {
    return this.searchString === searchString;
  }
  /**
   * Scroll Event Handler (Calculates if mpre items should be asked for from service on scrolling down)
   */
  onScroll() {
    if (this.maxResults > this.results.length) {
      let scrollAreaHeight = this.resultsListElement.nativeElement.offsetHeight;
      let scrollTopPos = this.resultsListElement.nativeElement.scrollTop;
      let scrollAreaMaxHeight = this.resultsListElement.nativeElement
        .scrollHeight;
      if (scrollTopPos + scrollAreaHeight * 2 >= scrollAreaMaxHeight) {
        this.getAdditionalResults();
      }
    }
  }

  /**
   * gets more results based when scrolling and adds the items
   */
  private getAdditionalResults() {
    this.showLoad = true;
    this.service
      .getDataByText(this.results.length, this.searchString)
      .subscribe((result) => {
        for (let i = 0; i < result.items.length; i++) {
          this.addResult(result.items[i]);
        }
        this.showLoad = false;
        this.maxResults = result.totalItems;
      });
  }

  /**
   * adds a single item to the list
   * @param item
   */
  private addResult(item: object) {
    //add check to make sure item does not exist
    this.results.push(item);
  }

  /**
   * Sets the highlighted item by keyboard or mouseover
   * @param item
   */
  private setHighlightedItem(item: Object): void {
    if (this.results && this.results.length > 0) {
      if (this.highlightedItem) {
        this.highlightedItem[this.HighlightedPropertyName] = false;
      }
      let message = '';
      if (item) {
        this.highlightedItem = item;
        this.highlightedItem[this.HighlightedPropertyName] = true;
        message = item[this.configuration.primaryTextField];
        if (
          this.configuration.secondaryTextField &&
          item[this.configuration.secondaryTextField]
        ) {
          message += ': ' + item[this.configuration.secondaryTextField];
        }
      } else {
        this.highlightedItem = undefined;
        message = 'No item selected';
      }
      this.addScreenReaderMessage(message);
    }
  }

  /**
   * Adds message to be read by screen reader
   * @param message
   */
  private addScreenReaderMessage(message: string) {
    this.srOnlyText = message;
  }

  writeValue(obj: any): void {
    if (obj instanceof SDSSelectedItemModel) {
      this.model = obj as SDSSelectedItemModel;
      this._changeDetectorRef.markForCheck();
      if (this.model.items.length === 0) {
        this.inputValue = '';
      } else if (
        this.configuration &&
        this.configuration.selectionMode === SelectionMode.SINGLE
      ) {
        this.inputValue = this.getObjectValue(
          this.model.items[0],
          this.configuration.primaryTextField
        );
      }
    }
  }

  addListener() {
    const autocompleteElement = document.getElementById(this.configuration.id);
    const dialogContainer = document.getElementsByClassName(
      'sds-dialog-content'
    );
    const resultsDropdown = document.getElementsByClassName('sds-autocomplete');

    let inputHeight = autocompleteElement.getBoundingClientRect().height;
    let modalHeight = dialogContainer[0].getBoundingClientRect().height;
    dialogContainer[0].addEventListener('scroll', function (event) {
      if (resultsDropdown.length > 0) {
        let inputTopValue = autocompleteElement.getBoundingClientRect().top;
        let inputBottomValue = modalHeight - inputTopValue;
        let listHeight = resultsDropdown[0].getBoundingClientRect().height;
        let element = resultsDropdown[0].parentElement;
        if (element && element.style.bottom) {
          element.style.bottom =
            inputBottomValue + listHeight - inputHeight + 30 + 'px';
        } else {
          element.style.top = inputTopValue + inputHeight + 'px';
        }
      }
    });
  }

  getClass() {
    return this.configuration.inputReadOnly ? 'hide-cursor' : '';
  }

  registerOnChange(fn: any): void {
    this.propogateChange = fn;
  }

  registerOnTouched(fn: any): void {
    this.onTouchedCallback = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }
}

