import { DOCUMENT } from '@angular/common';
import {
    Component,
    forwardRef,
    ChangeDetectionStrategy,
    ChangeDetectorRef,
    Input,
    ViewChild,
    ElementRef,
    Inject
} from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';

@Component({
    selector: 'sds-editor',
    template: `
    <div
      #searchInput
      [attr.id]="id"
      class="minh-15 border-gray-70 border-1px margin-top-1 padding-05"
      contenteditable="true"
      (input)="!validateOnBlur && onValueChange($event.target.innerHTML)"
      (blur)="validateOnBlur && onBlur($event.target.innerHTML)"
    >
    </div>
  `,
    providers: [
        {
            provide: NG_VALUE_ACCESSOR,
            useExisting: forwardRef(() => SdsEditorComponent),
            multi: true,
        },
    ],
    styles: [`
    ::ng-deep mark {
        background-color: red;
        color: white;
    }`
    ],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class SdsEditorComponent implements ControlValueAccessor {
    @ViewChild('searchInput', { read: ElementRef, static: true }) searchInput: ElementRef;

    @Input() id = 'searchEditor';
    @Input() regex = '';
    @Input() validateOnBlur = false;

    model = '';

    private _onChange = (_: any) => { };
    private _onTouched = () => { };

    constructor(private cd: ChangeDetectorRef, @Inject(DOCUMENT) private _document: any) {
    }

    // Helper method to programatically update a value of the model on blur
    onBlur(value) {
        this.model = value;
        if (this.regex) {
            this.validateRegex(this.model);
        }
        this.updateModel();
    }

    // Helper method that gets a new instance of the model and notifies ControlValueAccessor that we have a new model for this FormControl (our custom component)
    updateModel() {
        const model = this.getModel();
        this._onChange(model);
    }

    // Helper method to return a new instance of an value
    getModel() {
        return this.model;
    }

    // Validate regex and highlight first charecter of the failure
    validateRegex(value) {
        const rawValue = value
            .replaceAll(/<\/?mark[^>]*>/g, '')
            .replaceAll(/<\/?span[^>]*>/g, '')
            .replaceAll(/<\/?font[^>]*>/g, '');
        const regex = new RegExp(this.regex, 'g');
        let res = "";
        let result = regex.exec(rawValue);
        if (result) {
            let index = result.index;
            res = rawValue.substring(0, index) +
                '<mark>' + rawValue.substring(index, index + 1) +
                '</mark>' + rawValue.substring(index + 1, index + rawValue.length);
            this.searchInput.nativeElement.innerHTML = res;
        } else {
            this.searchInput.nativeElement.innerHTML = rawValue;
        }
    }

    // ControlValueAccessor (and Formly) is trying to update the value of the FormControl (our custom component) programatically
    // If there is a value we will just overwrite items
    // If there is no value we reset the items array to be empty
    writeValue(value: any) {
        if (value) {
            this.model = value;
            if (this.searchInput)
                this.searchInput.nativeElement.innerHTML = value;
            this.cd.markForCheck();
        } else {
            this.model = '';
            if (this.searchInput) this.searchInput.nativeElement.innerHTML = '';
            this.cd.markForCheck();
        }
    }

    // ControlValueAccessor hook that lets us call this._onChange(let) to let the form know our variable has changed (in this case model)
    registerOnChange(fn: any): void {
        this._onChange = fn;
    }

    // ControlValueAccessor hook (not used)
    registerOnTouched(fn: any) {
        this._onTouched = fn;
    }


    // Get the cursor current position 
    getCaretCharacterOffsetWithin(element) {
        let caretOffset = 0;
        if (typeof window.getSelection != 'undefined') {
            let range = window.getSelection().getRangeAt(0);
            let preCaretRange = range.cloneRange();
            preCaretRange.selectNodeContents(element);
            preCaretRange.setEnd(range.endContainer, range.endOffset);
            caretOffset = preCaretRange.toString().length;
        }
        return caretOffset;
    }

    onValueChange(value) {
        const pos = this.getCaretCharacterOffsetWithin(
            this.searchInput.nativeElement
        );
        if (this.regex) {
            this.validateRegex(value);
        }
        this.model = value;
        this.updateModel();

        let node = this.searchInput.nativeElement;
        if (pos < node?.innerText?.length) {
            let firstNodeLength = node.childNodes[0].textContent.length;

            let childNodeIndex =
                pos <= firstNodeLength ? 0 : 2;

            let startPosition =
                childNodeIndex === 0 ? pos : pos - firstNodeLength - 1;
            let range = this._document.createRange();
            let sel = window.getSelection();
            range.setStart(
                this.searchInput.nativeElement.childNodes[childNodeIndex],
                startPosition
            );
            range.collapse(true);
            sel.removeAllRanges();
            sel.addRange(range);
            this.searchInput.nativeElement.focus();
        } else {
            this._document.execCommand('selectAll', false, null);
            this._document.getSelection().collapseToEnd();
        }

    }
}

import {
  AfterContentInit,
  ChangeDetectionStrategy,
  Component,
  ElementRef,
  EventEmitter,
  InjectionToken,
  Input,
  OnDestroy,
  Output,
  TemplateRef,
  ViewChild,
  ViewEncapsulation,
  OnInit
} from '@angular/core';
import { AnimationEvent } from '@angular/animations';
import {
  ESCAPE,
  DOWN_ARROW,
  UP_ARROW,
  HOME,
  END,
  hasModifierKey
} from '@angular/cdk/keycodes';
import { FocusKeyManager, FocusOrigin } from '@angular/cdk/a11y';
import { coerceBooleanProperty } from '@angular/cdk/coercion';
import { Subscription } from 'rxjs';
import { sdsMenuAnimations } from './menu-animations';
import { SdsMenuItemComponent } from './menu-item.component';

/** Menu Positions */
export type MenuPositionX = 'before' | 'after';
export type MenuPositionY = 'above' | 'below';

/** Menu available sizes */
// sm = 'small'
export type MenuSizes = 'sm';

/** Injection token used to provide the parent menu to menu items. */
export const SDS_MENU_TOKEN = new InjectionToken<SdsMenuInterface>(
  'SDS_MENU_TOKEN'
);

/** Menu Interface */
export interface SdsMenuInterface<T = any> {
  xPosition: MenuPositionX;
  yPosition: MenuPositionY;
  overlapTrigger: boolean;
  templateRef: TemplateRef<any>;
  closed: EventEmitter<void | 'click' | 'keydown' | 'tab'>;
  parentMenu?: SdsMenuInterface;
  size?: MenuSizes;
  focusFirstItem: (origin?: FocusOrigin) => void;
  setPositionClasses?: (x: MenuPositionX, y: MenuPositionY) => void;
  addItem?: (item: T) => void;
  insertItem?: (item: T, index: number) => void;
  removeItem?: (item: T) => void;
}

@Component({
  selector: 'sds-menu',
  exportAs: 'sdsMenu',
  templateUrl: 'menu.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  encapsulation: ViewEncapsulation.None,
  animations: [sdsMenuAnimations.transformMenu],
  providers: [
    { provide: SDS_MENU_TOKEN, useExisting: SdsMenuComponent }
  ]
})
export class SdsMenuComponent
  implements
  OnInit,
  AfterContentInit,
  OnDestroy,
  SdsMenuInterface<SdsMenuItemComponent> {
  /** After | Before the menu triger element */
  private _xPosition: MenuPositionX = 'after';

  /** Above | Below the menu triger element */
  private _yPosition: MenuPositionY = 'below';

  /** Manage browser focus */
  private _keyManager: FocusKeyManager<SdsMenuItemComponent>;

  /** Menu items inside the current menu. */
  private _items: SdsMenuItemComponent[] = [];

  /** Subscription to tab events on the menu panel */
  private _tabSubscription = Subscription.EMPTY;

  /** Stores <sds-menu> classes */
  private _previousPanelClass: string;

  /** Config object to be passed into the menu's ngClass */
  _classList: { [key: string]: boolean } = {};

  /** Current state of the panel animation. */
  _panelAnimationState: 'void' | 'enter' = 'void';

  /** Grab the component template */
  @ViewChild(TemplateRef, { static: false }) templateRef: TemplateRef<any>;

  /**
   * Size of menu component.
   * Affects the font-size of the menu items and
   * the size of the close button in the menu header
   */
  @Input() size: MenuSizes;

  /** Position of the menu in the X axis. */
  @Input()
  get xPosition(): MenuPositionX {
    return this._xPosition;
  }
  set xPosition(value: MenuPositionX) {
    this._xPosition = value;
    this.setPositionClasses();
  }

  /** Position of the menu in the Y axis. */
  @Input()
  get yPosition(): MenuPositionY {
    return this._yPosition;
  }
  set yPosition(value: MenuPositionY) {
    this._yPosition = value;
    this.setPositionClasses();
  }

  /** Whether menu panel overlaps trigger element */
  @Input()
  get overlapTrigger(): boolean {
    return this._overlapTrigger;
  }
  set overlapTrigger(value: boolean) {
    this._overlapTrigger = coerceBooleanProperty(value);
  }
  private _overlapTrigger = false;

  /** Transfer classes from the sds-menu to the overlay container */
  @Input('class')
  set panelClass(classes: string) {
    const previousPanelClass = this._previousPanelClass;
    // Remove previous classes from current set of classes
    if (previousPanelClass && previousPanelClass.length) {
      previousPanelClass.split(' ').forEach((className: string) => {
        this._classList[className] = false;
      });
    }

    this._previousPanelClass = classes;

    // Adds new classes to current set of classes
    if (classes && classes.length) {
      classes.split(' ').forEach((className: string) => {
        this._classList[className] = true;
      });

      // Remove all classes from <sds-menu>
      this._elementRef.nativeElement.className = '';
    }
  }

  /** Event emitted when the menu is closed. */
  @Output() closed = new EventEmitter<void | 'click' | 'keydown' | 'tab'>();

  constructor(private _elementRef: ElementRef<HTMLElement>) { }

  ngOnInit() {
    this.setPositionClasses();
  }

  ngAfterContentInit() {
    this._keyManager = new FocusKeyManager<SdsMenuItemComponent>(
      this._items
    ).withWrap();
    this._tabSubscription = this._keyManager.tabOut.subscribe(() =>
      this.closed.emit('tab')
    );
  }

  ngOnDestroy() {
    this._tabSubscription.unsubscribe();
    this.closed.complete();
  }

  /** Focus the first item in the menu */
  focusFirstItem(origin: FocusOrigin = 'program'): void {
    this._keyManager.setFocusOrigin(origin).setFirstItemActive();
  }

  /** Adds classes to the menu panel based on its position */
  setPositionClasses(
    posX: MenuPositionX = this.xPosition,
    posY: MenuPositionY = this.yPosition
  ) {
    const classes = this._classList;
    classes['sds-menu-before'] = posX === 'before';
    classes['sds-menu-after'] = posX === 'after';
    classes['sds-menu-above'] = posY === 'above';
    classes['sds-menu-below'] = posY === 'below';
  }

  /** Adds a menu item with the menu. */
  addItem(item: SdsMenuItemComponent) {
    if (this._items.indexOf(item) === -1) {
      this._items.push(item);
    }
  }

  /** Inserts a menu item at an index */
  insertItem(item: SdsMenuItemComponent, index: number) {
    if (this._items.indexOf(item) === -1 && index < this._items.length) {
      this._items.splice(index, 0, item);
    }
  }

  /** Removes an item from the menu. */
  removeItem(item: SdsMenuItemComponent) {
    const index = this._items.indexOf(item);
    if (this._items.indexOf(item) > -1) {
      this._items.splice(index, 1);
    }
  }

  /** Handle a keyboard event from the menu */
  _handleKeydown(event: KeyboardEvent) {
    // tslint:disable-next-line: deprecation
    const keyCode = event.keyCode;
    const manager = this._keyManager;

    switch (keyCode) {
      case ESCAPE:
        this.closed.emit('keydown');
        break;
      case HOME:
      case END:
        if (!hasModifierKey(event)) {
          keyCode === HOME
            ? manager.setFirstItemActive()
            : manager.setLastItemActive();
          event.preventDefault();
        }
        break;
      default:
        if (keyCode === UP_ARROW || keyCode === DOWN_ARROW) {
          manager.setFocusOrigin('keyboard');
        }

        manager.onKeydown(event);
    }
  }

  /** Starts the enter animation. */
  _startAnimation() {
    this._panelAnimationState = 'enter';
  }

  /** Callback that is invoked when the panel animation completes. */
  _onAnimationDone(event: AnimationEvent) { }

  /** Resets the panel animation to its initial state. */
  _resetAnimation() {
    this._panelAnimationState = 'void';
  }

  _onAnimationStart(event: AnimationEvent) {
    // Scroll the content element to the top as soon as the animation starts.
    if (event.toState === 'enter' && this._keyManager.activeItemIndex === 0) {
      event.element.scrollTop = 0;
    }
  }
}

import { Component, Input } from '@angular/core';

@Component({
  selector: 'sds-menu-header',
  templateUrl: 'menu-header.component.html'
})
