import {
  Component,
  Input,
  ContentChild,
  TemplateRef,
  OnInit,
} from '@angular/core';
import { SearchModel } from './model/search-results.model';
import { Location } from '@angular/common';
@Component({
  selector: 'sds-search-result-list',
  templateUrl: './search-result-list.component.html',
  styleUrls: ['./search-result-list.component.scss'],
})
export class SdsSearchResultListComponent {
  public updateModel = new SearchModel();
  /**
   * Allow to insert a customized template for no results to use
   */
  @Input() customResultsTemplate: TemplateRef<any>;

  @Input() isDefaultModel: boolean;

  /**
   * Model for search results
   */
  @Input('model')
  set model(value) {
    if (Array.isArray(value)) {
      const items = value;
      this.updateModel = new SearchModel();
      this.updateModel.results = items;
    } else {
      this.updateModel = value;
    }
  }

  /**
   * Show divider between results
   */
  @Input() divider = true;

  constructor(private _location: Location) {}

  /**
   * Child Template to be used to display the data for each item in the list of items
   */
  @ContentChild('resultContent') resultContentTemplate: TemplateRef<any>;

  goBack() {
    this._location.back();
  }
  isTemplate() {
    if (this.updateModel && this.updateModel.metadata) {
      return Array.isArray(this.updateModel.metadata.messages);
    }
  }
}

import { Component, Input, TemplateRef, forwardRef } from '@angular/core';
import { SDSSelectedItemModel } from './models/sds-selectedItem.model';
import { SDSSelectedResultConfiguration } from './models/SDSSelectedResultConfiguration';
import { NG_VALUE_ACCESSOR, ControlValueAccessor } from '@angular/forms';
import { SDSSelectedItemModelHelper } from './models/sds-selected-item-model-helper';

const SDS_SelectedResult_VALUE_ACCESSOR: any = {
  provide: NG_VALUE_ACCESSOR,
  useExisting: forwardRef(() => SDSSelectedResultComponent),
  multi: true
};

@Component({
  selector: 'sds-selected-result',
  templateUrl: './selected-result.component.html',
  styleUrls: ['./selected-result.component.scss'],
  providers: [SDS_SelectedResult_VALUE_ACCESSOR]
})
export class SDSSelectedResultComponent implements ControlValueAccessor {


  /**
  * Allow to insert a customized template for suggestions to use
  */
  @Input() itemTemplate: TemplateRef<any>;

  /**
   * The data model that has the selected item
   */
  public model: SDSSelectedItemModel;


  /**
  * Configuration for the Selected Results control 
  */
  @Input()
  public configuration: SDSSelectedResultConfiguration;

  /**
   * Stored Event for ControlValueAccessor
   */
  public onTouchedCallback: () => void = () => null;

  /**
   * Stored Event for ControlValueAccessor
   */
  public propogateChange: (_: any) => void = (_: any) => null;

  @Input()
  public disabled: boolean;

  /**
   * Removes item from the model
   * @param item 
   */
  removeItem(item: object) {
    if (!this.disabled) {
      SDSSelectedItemModelHelper.removeItem(item, this.configuration.primaryKeyField, this.model);
      this.propogateChange(this.model);
      this.onTouchedCallback();
    }
  }

  writeValue(obj: any): void {
    if (obj instanceof SDSSelectedItemModel) {
      this.model = obj as SDSSelectedItemModel;
    }
  }

  registerOnChange(fn: any): void {
    this.propogateChange = fn;
  }

  registerOnTouched(fn: any): void {
    this.onTouchedCallback = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }


  /**
   * Gets the string value from the specifed properties of an object
   * @param object 
   * @param propertyFields comma seperated list with periods depth of object
   * @param index - the index location of the value in model's item list
   */
  getObjectValue(object: Object, propertyFields: string, index?: number): string {
    let value = '';
    let current = object;
    let fieldSplit = propertyFields.split(',');
    for (let i = 0; i < fieldSplit.length; i++) {
      let fieldValue = fieldSplit[i];
      let fieldPartSplit = fieldValue.split('.');
      for (let j = 0; j < fieldPartSplit.length; j++) {
        let fieldCheckValue = fieldPartSplit[j];
        if (current) {
          current = current[fieldCheckValue];
        }
      }

      if (current) {
        value += current.toString() + ' ';
      }
      current = object;
    }

    return this.configuration.displayModifierFn ? this.configuration.displayModifierFn(value.trim(), index) : value.trim();
  }

}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import { NavigationLink } from '../../side-navigation/model/side-navigation-model';
import { SelectionPanelModel } from '../model/selection-panel.model';

@Component({
  selector: 'sds-selection-panel',
  templateUrl: './selection-panel.component.html',
})
export class SdsSelectionPanelComponent {

  /**
   * Model containing navigation links as well as a selection mode property.
   * Navigation mode is implied if no selection mode is passed in
   */
  @Input() model: SelectionPanelModel;

  /**
   * True if users would prefer to navigate on panel selection, false otherwise.
   * Navigation feature is not supported on SELECTION mode. On NAVIGATION mode,
   * this is enabled by default
   */
  @Input() navigateOnClick: boolean;

  /**
   * Allows users to programatically select panel item. Can also be used
   * to select initial panel
   */
  @Input() currentSelection: NavigationLink;

  /**
   * Outputs a NavigationLink item when it is selected from the panel
   */
  @Output() panelSelected = new EventEmitter<NavigationLink>();
}

import { Component, ElementRef, EventEmitter, Input, OnChanges, Output, SimpleChanges, ViewChild } from '@angular/core';
import { ActivatedRoute, NavigationExtras, Router } from '@angular/router';
import { NavigationLink, SideNavigationModel } from '../../side-navigation/model/side-navigation-model';
import { SelectionPanelModel } from '../model/selection-panel.model';

@Component({
  selector: 'sds-selection-panel-navigation-mode',
  templateUrl: './navigation-mode.component.html',
  styleUrls: ['./navigation-mode.component.scss'],
})
export class SdsSelectionPanelNavigationModeComponent implements OnChanges {

  @Input() model: SelectionPanelModel;

  @Input() navigateOnClick = true;

  @Input() currentSelection: NavigationLink;

  @Output() panelSelected = new EventEmitter<NavigationLink>();

  constructor(
    private router: Router,
    private activatedRoute: ActivatedRoute,
  ) { }

  ngOnChanges(changes: SimpleChanges) {
    if (this.model && this.currentSelection && changes.currentSelection) {
      this.selectPanel(this.currentSelection);

      if (this.navigateOnClick) {
        this.navigateToSelectedItem(this.currentSelection);
      }
    }
  }

  onPanelItemClick(panelItem: NavigationLink) {
    this.selectPanel(panelItem);
    this.currentSelection = panelItem;

    if (this.navigateOnClick) {
      this.navigateToSelectedItem(panelItem);
    }

    this.panelSelected.emit(panelItem);
  }

  navigateToSelectedItem(selectedPanel: NavigationLink) {
    const navigationExtras: NavigationExtras = {
      queryParams: selectedPanel.queryParams,
      relativeTo: this.activatedRoute
    }

    this.router.navigateByUrl(selectedPanel.route, navigationExtras);
  }

  /** Public interface to select panel item */
  public selectPanel(panelItem: NavigationLink) {
    this.deselectSideNav(this.model.navigationLinks);
    this.selectSideNav(panelItem, this.model.navigationLinks);
  }

  /**
 * Deselects any previously selected sidenav item
 * @param sidenavItems
 */
  private deselectSideNav(sidenavItems: NavigationLink[]): void {
    sidenavItems.forEach(sideNavItem => {
      if (sideNavItem.children) {
        this.deselectSideNav(sideNavItem.children);
      }
      sideNavItem.selected = false;
    });
  }

  /**
   * Selects the clicked sidenav item as we as any parent
   * @param selectedItem
   * @param allNavItems
   */
  private selectSideNav(selectedItem: NavigationLink, allNavItems: NavigationLink[]): boolean {
    for (const item of allNavItems) {
      if (item === selectedItem) {
        item.selected = true;
        return true;
      } else if (item.children) {
        const isChildSelected = this.selectSideNav(selectedItem, item.children);
        if (isChildSelected) {
          item.selected = true;
          return true;
        }
      }
    }
    return false;
  }

}

