}


export class HeaderHome implements Selectable, INavigationLink {

    /**
     * Internal Angualr Routes, External HREF, EVENT: event on parent component
    */
    mode: NavigationMode;

    /**
     * Text for the Header
     */
    text: string;

    /**
     * Agency Logo for the Header
     */
