    /**
     * Navigation Route for Home image button
     */
    route: string;

    /**
    * Identifier for the item when search for selected
    */
    id: string;

    /**
     * Status of if the item is selected
     */
    selected?: boolean;
}

/**
 *
 */
export class HeaderNavigationLink implements Selectable, INavigationLink {

    /**
     * Internal Angualr Routes, External HREF, EVENT: event on parent component
     */
    mode: NavigationMode;

    /**
     * Text to be displayed in the link or button
     */
    text: string;

    /**
     * Navigation Route
     */
    route: string;

    /**
     * List of child navigation items that will show when no route is provieded
     */
    children?: HeaderNavigationLink[];

    /**
     * Identifier for the item when search for selected
     */
    id: string;

    /**
     * Status of if the item is selected
     */
    selected?: boolean;
}


export class HeaderSecondaryLink implements Selectable, INavigationLink {

    /**
     * Internal Angualr Routes, External HREF, EVENT: event on parent component
     */
    mode: NavigationMode;

    /**
     * Text to be displayed in the link
     */
    text: string;

    /**
     * Navigation Route
     */
    route: string;

    /**
     * image class eg. fas
     */
    imageClassPrefix: string;

    /**
     * image class
     */
    imageClass: string;

    /**
     * displays counter with image
     */
    hasCounter?: boolean;

    /**
     * Identifier for the item when search for selected
     */
    id: string;

    /**
     * Status of if the item is selected
     */
    selected?: boolean;
}

export interface INavigationLink {
    /**
     * Text to be displayed in the link
     */
    text: string;

    /**
     * Navigation Route 
     */
    route: string;

    /**
     * Internal Angualr Routes, External HREF, EVENT: event on parent component
     */
    mode: NavigationMode;
}

export enum NavigationMode {
    INTERNAL, EXTERNAL, EVENT, LABEL
}

export interface Selectable {
    /**
     * Identifier for the item when search for selected 
     */
    id: string;

    /**
     * Status of if the item is selected 
     */
    selected?: boolean;
}

import {InjectionToken} from '@angular/core';
import {CdkAccordion} from '@angular/cdk/accordion';

/** Accordion's display modes. */
export type SdsAccordionDisplayMode = 'default' | 'basic';

/**
 * Base interface for a `SdsAccordion`.
 */
export interface SdsAccordionBase extends CdkAccordion {
  /** Display mode used for all accordion items in the accordion. */
  displayMode: SdsAccordionDisplayMode;

  /** Handles keyboard events coming in from the item headers. */
  _handleHeaderKeydown: (event: KeyboardEvent) => void;

  /** Handles focus events on the item headers. */
  _handleHeaderFocus: (header: any) => void;
}


/**
 * Token used to provide a `SdsAccordion` to `SdsAccordionItem`.
 * Used primarily to avoid circular imports between `SdsAccordion` and `SdsAccordionItem`.
 */
export const SDS_ACCORDION = new InjectionToken<SdsAccordionBase>('SDS_ACCORDION');

