import {AnimationEvent} from '@angular/animations';
import { CdkAccordionItem } from "@angular/cdk/accordion";
import {UniqueSelectionDispatcher} from '@angular/cdk/collections';
import {TemplatePortal} from '@angular/cdk/portal';
import {
  AfterContentInit,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  ContentChild,
  EventEmitter,
  ElementRef,
  Inject,
  OnChanges,
  OnDestroy,
  Optional,
  Output,
  SimpleChanges,
  SkipSelf,
  ViewContainerRef,
  ViewEncapsulation,
  ViewChild
} from "@angular/core";
import {DOCUMENT} from '@angular/common';
import {ANIMATION_MODULE_TYPE} from '@angular/platform-browser/animations';
import {Subject} from 'rxjs';
import {filter, startWith, take, distinctUntilChanged} from 'rxjs/operators';
import {sdsExpansionAnimations} from './accordion-animations';
import {SdsAccordionItemContentDirective} from './accordion-item-content.directive';
import {SDS_ACCORDION, SdsAccordionBase} from './accordion-base';

/** Accordion Item's states. */
export type SdsAccordionItemState = 'expanded' | 'collapsed';

/** Counter for generating unique element ids. */
let uniqueId = 0;

@Component({
  selector: "sds-accordion-item",
  exportAs: "sdsAccordionItem",
  templateUrl: "accordion-item.component.html",
  encapsulation: ViewEncapsulation.None,
  changeDetection: ChangeDetectionStrategy.OnPush,
  inputs: ['disabled', 'expanded'],
  outputs: ['opened', 'closed', 'expandedChange'],
  animations: [sdsExpansionAnimations.bodyExpansion],
  host: {
    'class': 'sds-accordion__item',
    '[class.sds-accordion__item--expanded]': 'expanded',
    '[class._sds-animation-noopable]': '_animationMode === "NoopAnimations"'
  }
})
export class SdsAccordionItemComponent extends CdkAccordionItem
  implements AfterContentInit, OnChanges, OnDestroy {
  private _document: Document;

  /** An event emitted after the body's expansion animation happens. */
  @Output() afterExpand = new EventEmitter<void>();

  /** An event emitted after the body's collapse animation happens. */
  @Output() afterCollapse = new EventEmitter<void>();

  /** Stream that emits for changes in `@Input` properties. */
  readonly _inputChanges = new Subject<SimpleChanges>();

  /** Optionally defined accordion the accordion item belongs to. */
  accordion: SdsAccordionBase;

  /** Content that will be rendered lazily. */
  @ContentChild(SdsAccordionItemContentDirective) _lazyContent: SdsAccordionItemContentDirective;

  /** Element containing the accordion item's user-provided content. */
  @ViewChild('body') _body: ElementRef<HTMLElement>;

  /** Portal holding the user's content. */
  _portal: TemplatePortal;

  /** ID for the associated header element. Used for a11y labelling. */
  _headerId = `sds-accordion-item-header-${uniqueId++}`;

  /** Stream of body animation done events. */
  _bodyAnimationDone = new Subject<AnimationEvent>();

  constructor(@Optional() @SkipSelf() @Inject(SDS_ACCORDION) accordion: SdsAccordionBase,
              _changeDetectorRef: ChangeDetectorRef,
              _uniqueSelectionDispatcher: UniqueSelectionDispatcher,
              private _viewContainerRef: ViewContainerRef,
              @Inject(DOCUMENT) _document: any,
              @Optional() @Inject(ANIMATION_MODULE_TYPE) public _animationMode: string) {
    super(accordion, _changeDetectorRef, _uniqueSelectionDispatcher);
    this.accordion = accordion;
    this._document = _document;

    // We need a Subject with distinctUntilChanged, because the `done` event
    // fires twice on some browsers. See https://github.com/angular/angular/issues/24084
    this._bodyAnimationDone.pipe(distinctUntilChanged((x, y) => {
      return x.fromState === y.fromState && x.toState === y.toState;
    })).subscribe(event => {
      if (event.fromState !== 'void') {
        if (event.toState === 'expanded') {
          this.afterExpand.emit();
        } else if (event.toState === 'collapsed') {
          this.afterCollapse.emit();
        }
      }
    });
  }

  /** Gets the expanded state string. */
  _getExpandedState(): SdsAccordionItemState {
    return this.expanded ? 'expanded' : 'collapsed';
  }

  ngAfterContentInit() {
    if (this._lazyContent) {
      // Render the content as soon as the accordion item becomes open.
      this.opened.pipe(
        startWith(null!),
        filter(() => this.expanded && !this._portal),
        take(1)
      ).subscribe(() => {
        this._portal = new TemplatePortal(this._lazyContent._template, this._viewContainerRef);
      });
    }
  }

  ngOnChanges(changes: SimpleChanges) {
    this._inputChanges.next(changes);
  }

  ngOnDestroy() {
    super.ngOnDestroy();
    this._bodyAnimationDone.complete();
    this._inputChanges.complete();
  }

  /** Checks whether the accordion item's content contains the currently-focused element. */
  _containsFocus(): boolean {
    if (this._body) {
      const focusedElement = this._document.activeElement;
      const bodyElement = this._body.nativeElement;
      return focusedElement === bodyElement || bodyElement.contains(focusedElement);
    }

    return false;
  }
}

import { FocusMonitor, FocusableOption, FocusOrigin } from "@angular/cdk/a11y";
import { ENTER, SPACE, hasModifierKey } from "@angular/cdk/keycodes";
import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  ElementRef,
  Host,
  OnDestroy,
  ViewEncapsulation
} from "@angular/core";
import { merge, Subscription } from "rxjs";
import { filter } from "rxjs/operators";
import { SdsAccordionItemComponent } from "./accordion-item.component";

@Component({
  selector: "sds-accordion-item-header",
  templateUrl: "./accordion-item-header.component.html",
  styleUrls:['./accordion-item-header.component.scss'],
  encapsulation: ViewEncapsulation.None,
  changeDetection: ChangeDetectionStrategy.OnPush,
  host: {
    class: "sds-accordion__trigger",
    role: "button",
    "[attr.id]": "accordionItem._headerId",
    "[attr.tabindex]": "disabled ? -1 : 0",
    "[attr.aria-controls]": "_getAccordionItemId()",
    "[attr.aria-expanded]": "_isExpanded()",
    "[attr.aria-disabled]": "accordionItem.disabled",
    "(click)": "_toggle()",
    "(keydown)": "_keydown($event)"
  }
})
export class SdsAccordionItemHeaderComponent implements OnDestroy, FocusableOption {
  private _parentChangeSubscription = Subscription.EMPTY;
  constructor(
    @Host() public accordionItem: SdsAccordionItemComponent,
    private _element: ElementRef,
    private _focusMonitor: FocusMonitor,
    private _changeDetectorRef: ChangeDetectorRef
  ) {

    // Since the toggle state depends on an @Input on the accordion item, we
    // need to subscribe and trigger change detection manually.
    this._parentChangeSubscription = merge(
      accordionItem.opened,
      accordionItem.closed,
      accordionItem._inputChanges.pipe(
        filter(changes => !!(changes["disabled"]))
      )
    ).subscribe(() => this._changeDetectorRef.markForCheck());

    _focusMonitor.monitor(_element).subscribe(origin => {
      if (origin && accordionItem.accordion) {
        accordionItem.accordion._handleHeaderFocus(this);
      }
    });

  }

  /**
   * Whether the associated accordion item is disabled. 
   * Implemented as a part of `FocusableOption`.
   */
  get disabled() {
    return this.accordionItem.disabled;
  }

  /** Toggles the expanded state of the accordion item. */
  _toggle(): void {
    this.accordionItem.toggle();
  }

  /** Gets whether the accordion item is expanded. */
  _isExpanded(): boolean {
    return this.accordionItem.expanded;
  }

  /** Gets the accordion item id. */
  _getAccordionItemId(): string {
    return this.accordionItem.id;
  }

  /** Handle keydown event calling to toggle() if appropriate. */
  _keydown(event: KeyboardEvent) {
    switch (event.keyCode) {
      // Toggle for space and enter keys.
      case SPACE:
      case ENTER:
        if (!hasModifierKey(event)) {
          event.preventDefault();
          this._toggle();
        }

        break;
      default:
        if (this.accordionItem.accordion) {
          this.accordionItem.accordion._handleHeaderKeydown(event);
        }

        return;
    }
  }

  /**
   * Focuses the item header. Implemented as a part of `FocusableOption`.
   * @param origin Origin of the action that triggered the focus.
   * @docs-private
   */
  focus(origin: FocusOrigin = "program") {
    this._focusMonitor.focusVia(this._element, origin);
  }

  ngOnDestroy() {
    this._parentChangeSubscription.unsubscribe();
    this._focusMonitor.stopMonitoring(this._element);
  }
}

