import { Component, Input, TemplateRef, forwardRef } from '@angular/core';
import { SDSSelectedItemModel } from './models/sds-selectedItem.model';
import { SDSSelectedResultConfiguration } from './models/SDSSelectedResultConfiguration';
import { NG_VALUE_ACCESSOR, ControlValueAccessor } from '@angular/forms';
import { SDSSelectedItemModelHelper } from './models/sds-selected-item-model-helper';
const SDS_SelectedResult_VALUE_ACCESSOR: any = {
  provide: NG_VALUE_ACCESSOR,
  useExisting: forwardRef(() => SDSSelectedResultComponent),
  multi: true
};

@Component({
  selector: 'sds-selected-result',
  templateUrl: './selected-result.component.html',
  styleUrls: ['./selected-result.component.scss'],
  providers: [SDS_SelectedResult_VALUE_ACCESSOR]
})
export class SDSSelectedResultComponent implements ControlValueAccessor {

  /**
  * Allow to insert a customized template for suggestions to use
  */
  @Input() itemTemplate: TemplateRef<any>;

  /**
   * The data model that has the selected item
   */
  public model: SDSSelectedItemModel;


  /**
  * Configuration for the Selected Results control 
  */
  @Input()
  public configuration: SDSSelectedResultConfiguration;

  /**
   * Stored Event for ControlValueAccessor
   */
  public onTouchedCallback: () => void = () => null;

  /**
   * Stored Event for ControlValueAccessor
   */
  public propogateChange: (_: any) => void = (_: any) => null;

  @Input()
  public disabled: boolean;

  /**
   * Removes item from the model
   * @param item 
   */
  removeItem(item: object) {
    if (!this.disabled) {
      SDSSelectedItemModelHelper.removeItem(item, this.configuration.primaryKeyField, this.model.items);
      this.propogateChange(this.model);
      this.onTouchedCallback();
    }
  }

  writeValue(obj: any): void {
    if (obj instanceof SDSSelectedItemModel) {
      this.model = obj as SDSSelectedItemModel;
    }
  }

  registerOnChange(fn: any): void {
    this.propogateChange = fn;
  }

  registerOnTouched(fn: any): void {
    this.onTouchedCallback = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }


  /**
   * Gets the string value from the specifed properties of an object
   * @param object 
   * @param propertyFields comma seperated list with periods depth of object
   */
  getObjectValue(object: Object, propertyFields: string): string {
    let value = '';
    let current = object;
    let fieldSplit = propertyFields.split(',');
    for (let i = 0; i < fieldSplit.length; i++) {
      let fieldValue = fieldSplit[i];
      let fieldPartSplit = fieldValue.split('.');
      for (let j = 0; j < fieldPartSplit.length; j++) {
        let fieldCheckValue = fieldPartSplit[j];
        if (current) {
          current = current[fieldCheckValue];
        }
      }

      if (current) {
        value += current.toString() + ' ';
      }
      current = object;
    }
    return value.trim();
  }

}

import { Component, Input, Output, EventEmitter, ViewChild, TemplateRef } from '@angular/core';
import { SideNavigationModel, NavigationLink } from './model/side-navigation-model';
import { INavigationLink, NavigationMode } from '../common-navigation/common-navigation-model';
import { NavigationHelper } from '../common-navigation/navigation-helper';

@Component({
  selector: 'sds-side-navigation',
  templateUrl: './side-navigation.component.html',
  styleUrls: ['./side-navigation.component.scss']
})
export class SdsSideNavigationComponent {

  /**
   * Reference to the the Template used for internal links
   */
  @ViewChild('sideNavRouteLinkTemplate')
  private sideNavRouteLinkTemplate: TemplateRef<any>;

  /**
   * Reference to the the Template used for side menu items that are a label
   */
  @ViewChild('sideNavLabelLinkTemplate')
  private sideNavLabelLinkTemplate: TemplateRef<any>;

  /**
   * Reference to the the Template used for external href 
   */
  @ViewChild('sideNavHREFLinkTemplate')
  private sideNavHREFLinkTemplate: TemplateRef<any>;

  /**
   * Reference to the the Template used for event response
   */
  @ViewChild('sideNavEVENTLinkTemplate')
  private sideNavEVENTLinkTemplate: TemplateRef<any>;

  /**
   * Takes the navigation item and returns the template to be used
   * @param item navigation item
   */
  getItemTemplate(item: NavigationLink): TemplateRef<any> {
    let template = null;
    switch (item.mode) {
      case NavigationMode.EVENT:
        template = this.sideNavEVENTLinkTemplate;
        break;
      case NavigationMode.EXTERNAL:
        template = this.sideNavHREFLinkTemplate;
        break;
      case NavigationMode.INTERNAL:
        template = this.sideNavRouteLinkTemplate;
        break;
      case NavigationMode.LABEL:
        template = this.sideNavLabelLinkTemplate;
        break;
      default:
        template = null;
        break;
    }
    return template;
  }

  /**
   * Navigation helper
   */
  navigationHelper = new NavigationHelper();

  /**
   * Model used for the different display portions of the side navigation 
   */
  @Input() model: SideNavigationModel;

  /**
   * Selects new item and parents and deselects previous
   * @param id 
   */
  select(id: string) {
    this.deselect();
    for (let i = 0; i < this.model.navigationLinks.length; i++) {
      let item = this.model.navigationLinks[i];
      this.selectItem(id, item, null);
    }
  }

  /**
   * Selects item if matches passed in id and will select parent
   * @param id 
   * @param item 
   * @param parent 
   */
  private selectItem(id: string, item: NavigationLink, parent: NavigationLink) {
    if (item.id === id) {
      item.selected = true;
      if (parent) {
        parent.selected = true;
      }
    } else {
      if (item.children) {
        for (let i = 0; i < item.children.length; i++) {
          let childItem = item.children[i];
          this.selectItem(id, childItem, item);
        }
        if (item.selected) {
          if (parent) {
            parent.selected = true;
          }
        }
      }
    }
  }


  /**
   * Deselects all the items in the side navigation model
   */
  deselect() {
    if (this.model) {
      if (this.model.navigationLinks) {
        for (let i = 0; i < this.model.navigationLinks.length; i++) {
          this.deselectItem(this.model.navigationLinks[i]);
        }
      }
    }
  }

  /**
   * checks if item is selected and if selcted will check children and will unselect
   */
  private deselectItem(item: NavigationLink) {
    if (item.selected) {
      item.selected = false;
      if (item.children) {
        for (let i = 0; i < item.children.length; i++) {
          this.deselectItem(item.children[i]);
        }
      }
    }
  }

  /**
   * event for event based
   */
  @Output()
  linkEvent = new EventEmitter<INavigationLink>();

  /**
   * Link clicked and emits the link data into an event
   * @param link 
   */
  linkClickEvent(link: INavigationLink) {
    this.linkEvent.emit(link);
    return false;
  }

  /**
   * creates url from provided route and query params
   * @param item 
   */
  urlBuilder(item: NavigationLink) {
    let url = item.route;
    let queryParams = this.queryStringBuilder(item);
    if (queryParams) {
      if (url.indexOf('?') === -1) {
        url += '?' + queryParams;
      } else if (url.indexOf('?') === url.length - 1) {
        url += queryParams;
      } else {
        url += '&' + queryParams;
      }
    }
    return url;
  }

  /**
   * creates query string
   * @param item 
   */
  private queryStringBuilder(item: NavigationLink) {
    const ret = [];
    for (let d in item.queryParams) {
      ret.push(encodeURIComponent(d) + '=' + encodeURIComponent(item.queryParams[d]));
    }
    return ret.join('&');
  }
}

import { Component, EventEmitter, Input, Output } from "@angular/core";
import { coerceBooleanProperty } from "@angular/cdk/coercion";
import { sdsToolbarAnimations } from "./toolbar-animations";

/** Toolbar's states. */
export type SdsToolbarState = "expanded" | "collapsed";

/** Counter for generating unique element ids. */
let uniqueId = 0;

@Component({
  selector: "sds-toolbar",
  exportAs: "sdsToolbar",
  templateUrl: "toolbar.component.html",
  animations: [sdsToolbarAnimations.bodyExpansion],
  host: {
    class: "sds-toolbar",
    "[class.sds-toolbar--expanded]": "expanded"
  }
})
export class SdsToolbarComponent {
  /** The unique Toolbar id. */
  readonly id = uniqueId++;

  /** ID for the header element. Used for a11y labelling. */
  readonly _headerId: string = `sds-toolbar-header-${this.id}`;

  /** ID for the content element. Used for a11y labelling. */
  readonly _contentId: string = `sds-toolbar-content-${this.id}`;

  constructor() {}

  /** Emits whenever the expanded state of the toolbar changes. */
  @Output() expandedChange: EventEmitter<boolean> = new EventEmitter<boolean>();

  /** Whether the toolbar is expanded. */
  @Input()
  get expanded(): any {
    return this._expanded;
  }
  set expanded(expanded: any) {
    expanded = coerceBooleanProperty(expanded);

    // Only emit events and update the internal value if the value changes.
    if (this._expanded !== expanded) {
      this._expanded = expanded;
      this.expandedChange.emit(expanded);
    }
  }
  private _expanded = false;

  /** Whether the toolbar is disabled. */
  @Input()
  get disabled() {
    return this._disabled;
  }
  set disabled(disabled: any) {
    this._disabled = coerceBooleanProperty(disabled);
  }
  private _disabled: boolean = false;

  /** Width of the toolbar when is expanded. */
  @Input() expandedWidth: string = "300px";

  // Elements that surround the toolbar need to make space for the expanded toolbar.
  // Adding 20 more pixes to provide padding
  @Input() expandedSpace: string = "20px";

  /** Toggles the expanded state of the toolbar. */
  toggle() {
    this.expanded = !this.expanded;
  }

  /** Gets the expanded state string. */
  _getExpandedState(): SdsToolbarState {
    return this.expanded ? "expanded" : "collapsed";
  }
}

import { Component, Host } from "@angular/core";
import { SdsToolbarComponent } from "./toolbar.component";

@Component({
