   * class `dialog-slide-out` to container
   */
  slideOut?: boolean | SlideOutConfig = false;
}

export enum ActionMenuMode {
  SHOWN,
  DISABLED,
  HIDDEN,
}

export interface TriggerTypeModel {
  type: string;
  shadow: boolean;
}

export interface ActionMenuActions {
  id: string;
  text: string;
  mode?: ActionMenuMode;
}

export interface ActionMenuModel {
  trigger: TriggerTypeModel;
  label?: string;
  actions: ActionMenuActions[];
}

export interface VPInterface {
  sourceWebm: string;
  sourceMp4: string;
  height: string;
  width: string;
  caption: string;
  poster: string;
  id: string;
  seekInterval: number;
  debug: boolean;
  preload: string;
  description?: string;
}

