    id: 'searchOptions',
    placeholder: '-Select-',
    options: [],
    inverse: false,
  };
  public isSuffixSearchIcon?: boolean = false;
}
@Component({
  selector: 'sds-search',
  templateUrl: 'search.component.html',
  styleUrls: ['search.component.scss'],
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => SdsSearchComponent),
      multi: true,
    },
  ],

  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class SdsSearchComponent implements AfterViewInit, ControlValueAccessor {
  @ViewChild('inputEl', { read: ElementRef, static: false })
  inputEl: ElementRef;
  @ViewChild('selectEl', { read: ElementRef, static: false })
  selectEl: ElementRef;
  @ViewChild('buttonEl', { read: ElementRef, static: false })
  buttonEl: ElementRef;

  @Input() searchSettings: SearchSettings = new SearchSettings();
  @Output() submit: EventEmitter<{ searchText: string }> = new EventEmitter(null);

  model: any = {};
  inputState = {
    initial: { visible: undefined },
    visible: undefined,
  };
  onChange = (_: any) => {};
  onTouched = () => {};

  constructor(
    private cd: ChangeDetectorRef,
    private focusMonitor: FocusMonitor,
    private viewportRuler: ViewportRuler
  ) {}

  ngAfterViewInit() {
    this.inputState.initial.visible = this.isInputVisible();
    this.inputState.visible = this.inputState.initial.visible;
    this.viewportRuler.change(0).subscribe(() => {
      this.inputState.initial.visible = this.isInputVisible();
      this.inputState.visible = this.inputState.initial.visible;
    });
  }

  hasDropdown() {
    if (
      this.searchSettings &&
      this.searchSettings.dropdown &&
      this.searchSettings.dropdown.options &&
      this.searchSettings.dropdown.options.length
    ) {
      return true;
    } else {
      return false;
    }
  }
  handleClick(event) {
    event.preventDefault();
    if (!this.inputState.visible) {
      this.setInputVisibleStyles();
      this.focusMonitor.focusVia(this.inputEl, 'program');
    } else if (this.inputEl || this.selectEl) {
      this.submit.emit(this.model);
    }
  }

  writeValueToModel() {
    this.model.searchText = this.inputEl ? this.inputEl.nativeElement.value : '';
    if (this.selectEl && this.selectEl.nativeElement.value) {
      this.model.searchCategory = this.selectEl.nativeElement.value;
    }
    this.onChange(Object.assign({}, this.model));
  }

  writeValue(value: any) {
    if (value && this.model !== value) {
      this.model = value;
      this.cd.markForCheck();
    } else {
      this.model = {};
      this.cd.markForCheck();
    }
  }
  registerOnTouched(fn: any) {
    this.onTouched = fn;
  }
  registerOnChange(fn: any): void {
    this.onChange = fn;
  }

  isInputVisible(): boolean {
    return this.inputEl.nativeElement.getBoundingClientRect().width ? true : false;
  }

  setInputVisibleStyles() {
    const inputWidth = this.calculateInputWidth();
    this.inputEl.nativeElement.style.setProperty('display', 'block', 'important');
    this.inputEl.nativeElement.style.position = 'absolute';
    this.inputEl.nativeElement.style.left = `-${inputWidth}px`;
    this.inputEl.nativeElement.style.setProperty('width', `${inputWidth}px`, 'important');
    this.inputState.visible = true;
  }

  removeInputVisibleStyles() {
    this.inputEl.nativeElement.style.display = '';
    this.inputEl.nativeElement.style.position = '';
    this.inputEl.nativeElement.style.left = '';
    this.inputEl.nativeElement.style.width = '';
    this.inputState.visible = false;
  }

  focusChange() {
    if (!this.inputState.initial.visible) {
      this.removeInputVisibleStyles();
    }
  }

  calculateInputWidth(): number {
    const leftPadding = 20;
    const buttonElement = this.buttonEl.nativeElement;
    const inputElement = this.inputEl.nativeElement;
    const rightPosition = buttonElement.getBoundingClientRect().left;
    const leftPosition = this.searchSettings.parentSelector
      ? inputElement.closest(this.searchSettings.parentSelector).getBoundingClientRect().left
      : 0;
    return Math.floor(rightPosition - leftPosition - leftPadding);
  }
  getClass() {
    const cls = this.searchSettings && this.searchSettings.size === 'large' ? 'usa-search--big' : 'usa-search--small';
    return this.searchSettings.dropdown && this.searchSettings.dropdown.inverse ? `${cls} sds-inverse` : cls;
  }
  clearInput(ev) {
    this.inputEl.nativeElement.value = '';
    this.writeValueToModel();
  }
}

import { SideNavigationModel } from '../../side-navigation/model/side-navigation-model';

export class SelectionPanelModel extends SideNavigationModel {
  /**
   * The mode selected impacts which version of the selection panel is used.
   * 'SELECTION' mode will only display the top level navigation items and
   * will not display children items. 'NAVIGATION' mode will display the children
   * navigation items indented under its parent. If a mode is not passed in,
   * then 'NAVIGATION' mode is assumed.
   */
  selectionMode?: 'SELECTION' | 'NAVIGATION';
}

import { QueryParamsHandling } from '@angular/router';
import { INavigationLink, NavigationMode, Selectable } from '../../common-navigation/common-navigation-model';

export class SideNavigationModel {
  /**
   *
   */
  navigationLinks: NavigationLink[];
}

export class NavigationLink implements Selectable, INavigationLink {
  /**
   * Internal Angualr Routes, External HREF, EVENT: event on parent component
   */
  mode: NavigationMode;

  /**
   * Text to be displayed in the link or button
   */
  text: string;

  /**
   * Navigation Route
   */
  route: string;

  /**
   * List of child navigation items that will show when no route is provieded
   */
  children?: NavigationLink[];

  /**
   * Identifier for the item when search for selected
   */
  id: string;

  /**
   * Status of if the item is selected
   */
  selected?: boolean;

  /**
   * Query string paramaters supporeted with external and internal links
   * ex. { 'name': 'value',...}
   */
  queryParams?: {
    [k: string]: any;
  };

  queryParamsHandling?: QueryParamsHandling;
}

