.grayToLightBackgroundGradiate {
  background: linear-gradient(to right, #c4c4c4, #f7f7f7);
  background-size: 400% 400%;
  height: 10px;
  padding: 2.5px 0 2.5px 0;
  text-align: center;
  color: #888888;
  font-size: 15px;
  font-weight: bold;
  margin: 7px 10px 0 10px;
  -webkit-animation: gradiantShiftGray 5s ease infinite;
  -moz-animation: gradiantShiftGray 5s ease infinite;
  animation: gradiantShiftGray 5s ease infinite;

  @-webkit-keyframes gradiantShiftGray {
    0% {
      background-position: 0% 50%;
    }

    50% {
      background-position: 100% 50%;
    }

    100% {
      background-position: 0% 50%;
    }
  }

  @-moz-keyframes gradiantShiftGray {
    0% {
      background-position: 0% 50%;
    }

    50% {
      background-position: 100% 50%;
    }

    100% {
      background-position: 0% 50%;
    }
  }

  @keyframes gradiantShiftGray {
    0% {
      background-position: 0% 50%;
    }

    50% {
      background-position: 100% 50%;
    }

    100% {
      background-position: 0% 50%;
    }
  }
}

.lightToGrayBackgroundGradiate {
  background: linear-gradient(to right, #f7f7f7, #c4c4c4);
  background-size: 400% 400%;
  height: 15px;
  padding: 2.5px 0 2.5px 0;
  text-align: center;
  color: #888888;
  font-size: 15px;
  font-weight: bold;
  margin: 10px 10px 0 10px;
  -webkit-animation: gradiantShift 5s ease infinite;
  -moz-animation: gradiantShift 5s ease infinite;
  animation: gradiantShift 5s ease infinite;

  @-webkit-keyframes gradiantShift {
    0% {
      background-position: 0% 50%;
    }

    50% {
      background-position: 100% 50%;
    }

    100% {
      background-position: 0% 50%;
    }
  }

  @-moz-keyframes gradiantShift {
    0% {
      background-position: 0% 50%;
    }

    50% {
      background-position: 100% 50%;
    }

    100% {
      background-position: 0% 50%;
    }
  }

  @keyframes gradiantShift {
    0% {
      background-position: 0% 50%;
    }

    50% {
      background-position: 100% 50%;
    }

    100% {
      background-position: 0% 50%;
    }
  }
}

.autoLoading {
  height: 60px;
}

.hide-cursor {
  caret-color: transparent;
}

input::-ms-clear {
  display: none;
}

