  width: 8px;
  background-color: #666;
}

/* fixing display for IE10+ */
@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
  .px-video-controls input[type='range'] {
    position: relative;
    padding: 0;
    height: 8px;
    top: -3px;
  }

  .px-video-time {
    margin-top: 4px;
  }

  .px-video-captions {
    padding: 8px;
    min-height: 36px;
  }
}

.px-video-container {
  position: relative;
  min-width: 200px;
}

#videoPlayer {
  min-width: 200px;
}

.px-video-container.fullscreen {
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  -webkit-cursor-visibility: auto-hide;
}

/* Fullscreen styles */

/* style applied through js */
.px-video-controls.js-fullscreen-controls {
  position: absolute;
  bottom: 0;
  width: 100%;
  z-index: 940;
  background: white;
}

.px-video-captions.js-fullscreen-captions {
  min-height: 3.5em;
  font-size: 2.5em;
  padding: 1em;
}

.px-timetip {
  padding: 5px 10px;
  font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
  font-size: 14px;
  background: rgba(0, 0, 0, 0.83);
  border-radius: 3px;
  color: #fff;
  width: auto;
  bottom: 20px;
}

.px-timetip:after {
  top: 100%;
  left: 50%;
  border: solid transparent;
  content: ' ';
  height: 0;
  width: 0;
  position: absolute;
  pointer-events: none;
  border-color: rgba(43, 43, 43, 0);
  border-top-color: #2b2b2b;
  border-width: 10px;
  margin-left: -10px;
}

