{
  /** Animation that expands and collapses the accordion item content. */
  bodyExpansion: trigger('bodyExpansion', [
    state('collapsed, void', style({ height: '0px', visibility: 'hidden' })),
    state('expanded', style({ height: '*', visibility: 'visible' })),
    transition('expanded <=> collapsed, void => collapsed', animate(ACCORDION_ITEM_ANIMATION_TIMING)),
  ]),
}

{
  transformMenu: trigger('transformMenu', [
    state(
      'void',
      style({
        opacity: 0,
        transform: 'scale(0.8)',
      })
    ),
    transition(
      'void => enter',
      group([
        query(
          '.sds-menu',
          animate(
            '100ms linear',
            style({
              opacity: 1,
            })
          )
        ),
        animate('120ms cubic-bezier(0, 0, 0.2, 1)', style({ transform: 'scale(1)' })),
      ])
    ),
    transition('* => void', animate('100ms 25ms linear', style({ opacity: 0 }))),
  ]),
}

{
  toastComponent: SdsToastComponent,
  timeOut: 6000,
  toastClass: 'sds-toast',
  positionClass: 'toast-bottom-left',
}

{
  container: trigger('container', [
    state(
      'void',
      style({
        opacity: 0,
        transform: 'scale(0.8)',
      })
    ),
    transition(
      'void => enter',
      group([
        query(
          '.sds-overlay',
          animate(
            '100ms linear',
            style({
              opacity: 1,
            })
          )
        ),
        animate('120ms cubic-bezier(0, 0, 0.2, 1)', style({ transform: 'scale(1)' })),
      ])
    ),
    transition('* => void', animate('100ms 25ms linear', style({ opacity: 0 }))),
  ]),
}

